dataSet.query:
SELECT TOP 1 ChartOfAccounts.Ref AS Account INTO RetainedEarningsAccount FROM ChartOfAccounts.ChartOfAccounts AS ChartOfAccounts WHERE ChartOfAccounts.RetainedEarnings = TRUE ;
//////////////////////////////////////////////////////////////////////////////// SELECT YearsOpeningBalance.Account AS Account, YearsOpeningBalance.Period AS YearPeriod, DATEADD(YearsOpeningBalance.Period, MONTH, -&IncrementMonth) AS FiscalPeriod, YEAR(DATEADD(YearsOpeningBalance.Period, MONTH, -&IncrementMonth)) AS FiscalYear, YearsOpeningBalance.AmountRCOpeningBalance AS YearOpeningBalance, YearsOpeningBalance.AmountRCClosingBalance AS YearClosingBalance INTO FiscalYearsBalanceA FROM AccountingRegister.GeneralJournal.BalanceAndTurnovers({(DATEADD(&BeginOfPeriod, MONTH, -12))}, {(&EndOfPeriod)}, Month, , , , ) AS YearsOpeningBalance
INDEX BY Account, FiscalPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT FiscalYearsBalanceA.Account AS Account, FiscalYearsBalanceA.FiscalYear, MIN(FiscalYearsBalanceA.FiscalPeriod) AS BeginFiscalYear, NULL AS EndFiscalYear INTO FiscalYearsBalanceB FROM FiscalYearsBalanceA AS FiscalYearsBalanceA
GROUP BY FiscalYearsBalanceA.Account, FiscalYearsBalanceA.FiscalYear
UNION ALL
SELECT FiscalYearsBalanceA.Account, FiscalYearsBalanceA.FiscalYear, NULL, MAX(FiscalYearsBalanceA.FiscalPeriod) FROM FiscalYearsBalanceA AS FiscalYearsBalanceA
GROUP BY FiscalYearsBalanceA.Account, FiscalYearsBalanceA.FiscalYear
INDEX BY Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT FiscalYearsBalanceB.Account, FiscalYearsBalanceB.FiscalYear, MAX(DATEADD(BEGINOFPERIOD(FiscalYearsBalanceB.BeginFiscalYear, YEAR), MONTH, 12 + &IncrementMonth)) AS BeginFiscalYear, MAX(DATEADD(ENDOFPERIOD(FiscalYearsBalanceB.EndFiscalYear, YEAR), MONTH, 12 + &IncrementMonth)) AS EndFiscalYear, SUM(CASE WHEN FiscalYearsBalanceB.EndFiscalYear IS NULL THEN FiscalYearsBalanceA.YearOpeningBalance ELSE NULL END) AS YearOpeningBalance, SUM(CASE WHEN FiscalYearsBalanceB.BeginFiscalYear IS NULL THEN FiscalYearsBalanceA.YearClosingBalance ELSE NULL END) AS YearClosingBalance INTO FiscalYearsBalanceC FROM FiscalYearsBalanceB AS FiscalYearsBalanceB INNER JOIN FiscalYearsBalanceA AS FiscalYearsBalanceA ON FiscalYearsBalanceB.Account = FiscalYearsBalanceA.Account AND (FiscalYearsBalanceB.BeginFiscalYear = FiscalYearsBalanceA.FiscalPeriod OR FiscalYearsBalanceB.EndFiscalYear = FiscalYearsBalanceA.FiscalPeriod)
GROUP BY FiscalYearsBalanceB.Account, FiscalYearsBalanceB.FiscalYear ;
//////////////////////////////////////////////////////////////////////////////// SELECT FiscalYearsBalanceC.Account AS Account, FiscalYearsBalanceC.FiscalYear AS FiscalYear, BEGINOFPERIOD(FiscalYearsBalanceC.BeginFiscalYear, DAY) AS DayPeriod, BEGINOFPERIOD(FiscalYearsBalanceC.BeginFiscalYear, WEEK) AS WeekPeriod, BEGINOFPERIOD(FiscalYearsBalanceC.BeginFiscalYear, MONTH) AS MonthPeriod, BEGINOFPERIOD(FiscalYearsBalanceC.BeginFiscalYear, QUARTER) AS QuarterPeriod, BEGINOFPERIOD(FiscalYearsBalanceC.BeginFiscalYear, YEAR) AS YearPeriod, FiscalYearsBalanceC.YearOpeningBalance, FiscalYearsBalanceC.YearClosingBalance INTO YearsOpeningBalance {SELECT Account.*, FiscalYear, DayPeriod, WeekPeriod, MonthPeriod, QuarterPeriod, YearPeriod} FROM FiscalYearsBalanceC AS FiscalYearsBalanceC {WHERE FiscalYearsBalanceC.Account.*}
INDEX BY DayPeriod, FiscalYearsBalanceC.Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalBalanceAndTurnovers.Account AS Account, YEAR(DATEADD(GeneralJournalBalanceAndTurnovers.DayPeriod, MONTH, -&IncrementMonth)) - 1 AS FiscalYear, GeneralJournalBalanceAndTurnovers.DayPeriod AS DayPeriod, BEGINOFPERIOD(GeneralJournalBalanceAndTurnovers.DayPeriod, WEEK) AS WeekPeriod, BEGINOFPERIOD(GeneralJournalBalanceAndTurnovers.DayPeriod, MONTH) AS MonthPeriod, BEGINOFPERIOD(GeneralJournalBalanceAndTurnovers.DayPeriod, QUARTER) AS QuarterPeriod, BEGINOFPERIOD(GeneralJournalBalanceAndTurnovers.DayPeriod, YEAR) AS YearPeriod, GeneralJournalBalanceAndTurnovers.AmountRCOpeningBalance, GeneralJournalBalanceAndTurnovers.AmountRCClosingBalance, GeneralJournalBalanceAndTurnovers.AmountRCTurnover INTO GeneralJournalBalanceAndTurnovers {SELECT Account.*, DayPeriod, WeekPeriod, MonthPeriod, QuarterPeriod, YearPeriod} FROM AccountingRegister.GeneralJournal.BalanceAndTurnovers({(&BeginOfPeriod)}, {(&EndOfPeriod)}, Auto, , , , ) AS GeneralJournalBalanceAndTurnovers {WHERE GeneralJournalBalanceAndTurnovers.Account.*}
INDEX BY DayPeriod, Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT YearsOpeningBalance.Account AS Account, YearsOpeningBalance.FiscalYear AS FiscalYear, YearsOpeningBalance.DayPeriod AS DayPeriod, YearsOpeningBalance.WeekPeriod, YearsOpeningBalance.MonthPeriod, YearsOpeningBalance.QuarterPeriod, YearsOpeningBalance.YearPeriod INTO GeneralJournalSummaryA FROM YearsOpeningBalance AS YearsOpeningBalance
UNION ALL
SELECT GeneralJournalBalanceAndTurnovers.Account, GeneralJournalBalanceAndTurnovers.FiscalYear, GeneralJournalBalanceAndTurnovers.DayPeriod, GeneralJournalBalanceAndTurnovers.WeekPeriod, GeneralJournalBalanceAndTurnovers.MonthPeriod, GeneralJournalBalanceAndTurnovers.QuarterPeriod, GeneralJournalBalanceAndTurnovers.YearPeriod FROM GeneralJournalBalanceAndTurnovers AS GeneralJournalBalanceAndTurnovers ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalSummaryA.Account AS Account, GeneralJournalSummaryA.FiscalYear AS FiscalYear, GeneralJournalSummaryA.DayPeriod AS DayPeriod, GeneralJournalSummaryA.WeekPeriod, GeneralJournalSummaryA.MonthPeriod, GeneralJournalSummaryA.QuarterPeriod, GeneralJournalSummaryA.YearPeriod INTO GeneralJournalSummaryB FROM GeneralJournalSummaryA AS GeneralJournalSummaryA
GROUP BY GeneralJournalSummaryA.Account, GeneralJournalSummaryA.FiscalYear, GeneralJournalSummaryA.DayPeriod, GeneralJournalSummaryA.QuarterPeriod, GeneralJournalSummaryA.YearPeriod, GeneralJournalSummaryA.MonthPeriod, GeneralJournalSummaryA.WeekPeriod
INDEX BY DayPeriod, Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalSummaryB.Account AS Account, GeneralJournalSummaryB.FiscalYear AS FiscalYear, GeneralJournalSummaryB.DayPeriod AS DayPeriod, GeneralJournalSummaryB.WeekPeriod, GeneralJournalSummaryB.MonthPeriod, GeneralJournalSummaryB.QuarterPeriod, GeneralJournalSummaryB.YearPeriod, MAX(YearsOpeningBalance.DayPeriod) AS DayPeriodYears, MAX(GeneralJournalBalanceAndTurnovers.DayPeriod) AS DayPeriodGeneralJournal INTO GeneralJournalSummaryC FROM GeneralJournalSummaryB AS GeneralJournalSummaryB LEFT JOIN YearsOpeningBalance AS YearsOpeningBalance ON GeneralJournalSummaryB.DayPeriod >= YearsOpeningBalance.DayPeriod AND GeneralJournalSummaryB.Account = YearsOpeningBalance.Account LEFT JOIN GeneralJournalBalanceAndTurnovers AS GeneralJournalBalanceAndTurnovers ON GeneralJournalSummaryB.DayPeriod >= GeneralJournalBalanceAndTurnovers.DayPeriod AND GeneralJournalSummaryB.Account = GeneralJournalBalanceAndTurnovers.Account
GROUP BY GeneralJournalSummaryB.Account, GeneralJournalSummaryB.FiscalYear, GeneralJournalSummaryB.DayPeriod, GeneralJournalSummaryB.QuarterPeriod, GeneralJournalSummaryB.YearPeriod, GeneralJournalSummaryB.MonthPeriod, GeneralJournalSummaryB.WeekPeriod
INDEX BY Account, DayPeriodYears, DayPeriodGeneralJournal ;
//////////////////////////////////////////////////////////////////////////////// SELECT CASE WHEN GeneralJournalSummaryC.Account.AccountType IN (VALUE(Enum.AccountTypes.Bank), VALUE(Enum.AccountTypes.Inventory), VALUE(Enum.AccountTypes.AccountsReceivable), VALUE(Enum.AccountTypes.OtherCurrentAsset)) THEN "Current Assets" WHEN GeneralJournalSummaryC.Account.AccountType IN (VALUE(Enum.AccountTypes.FixedAsset), VALUE(Enum.AccountTypes.AccumulatedDepreciation), VALUE(Enum.AccountTypes.OtherNoncurrentAsset)) THEN "Noncurrent Assets" WHEN GeneralJournalSummaryC.Account.AccountType IN (VALUE(Enum.AccountTypes.AccountsPayable), VALUE(Enum.AccountTypes.OtherCurrentLiability)) THEN "Current Liabilities" WHEN GeneralJournalSummaryC.Account.AccountType IN (VALUE(Enum.AccountTypes.LongTermLiability)) THEN "Long Term Liabilities" WHEN GeneralJournalSummaryC.Account.AccountType IN (VALUE(Enum.AccountTypes.Equity)) THEN "Stockholder Equity" WHEN GeneralJournalSummaryC.Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.IncomeTaxExpense)) THEN "Stockholder Equity" ELSE NULL END AS Section, GeneralJournalSummaryC.Account AS Account, GeneralJournalSummaryC.FiscalYear AS FiscalYear, GeneralJournalSummaryC.DayPeriod AS DayPeriod, GeneralJournalSummaryC.WeekPeriod, GeneralJournalSummaryC.MonthPeriod, GeneralJournalSummaryC.QuarterPeriod, GeneralJournalSummaryC.YearPeriod, GeneralJournalBalanceAndTurnovers.AmountRCClosingBalance, CASE WHEN GeneralJournalSummaryC.DayPeriod = GeneralJournalBalanceAndTurnovers.DayPeriod THEN GeneralJournalBalanceAndTurnovers.AmountRCOpeningBalance ELSE GeneralJournalBalanceAndTurnovers.AmountRCClosingBalance END AS AmountRCOpeningBalance, CASE WHEN GeneralJournalSummaryC.DayPeriod = GeneralJournalBalanceAndTurnovers.DayPeriod THEN GeneralJournalBalanceAndTurnovers.AmountRCTurnover ELSE NULL END AS AmountRCTurnover, CASE WHEN GeneralJournalSummaryC.DayPeriod = YearsOpeningBalance.DayPeriod THEN YearsOpeningBalance.YearOpeningBalance ELSE YearsOpeningBalance.YearClosingBalance END AS YearOpeningBalance, YearsOpeningBalance.YearClosingBalance INTO GeneralJournalSummary FROM GeneralJournalSummaryC AS GeneralJournalSummaryC LEFT JOIN YearsOpeningBalance AS YearsOpeningBalance ON GeneralJournalSummaryC.Account = YearsOpeningBalance.Account AND GeneralJournalSummaryC.DayPeriodYears = YearsOpeningBalance.DayPeriod LEFT JOIN GeneralJournalBalanceAndTurnovers AS GeneralJournalBalanceAndTurnovers ON GeneralJournalSummaryC.Account = GeneralJournalBalanceAndTurnovers.Account AND GeneralJournalSummaryC.DayPeriodGeneralJournal = GeneralJournalBalanceAndTurnovers.DayPeriod {WHERE (GeneralJournalSummaryC.YearPeriod <= &EndOfPeriod OR GeneralJournalSummaryC.YearPeriod = DATETIME(3999, 1, 1))}
INDEX BY Section ;
//////////////////////////////////////////////////////////////////////////////// SELECT "Assets" AS Division, "Current Assets" AS Section, 1 AS SectionOrder INTO BalanceSections
UNION ALL
SELECT "Assets", "Noncurrent Assets", 2
UNION ALL
SELECT "Liabilities and Stockholder Equity", "Current Liabilities", 3
UNION ALL
SELECT "Liabilities and Stockholder Equity", "Long Term Liabilities", 4
UNION ALL
SELECT "Liabilities and Stockholder Equity", "Stockholder Equity", 5 ;
//////////////////////////////////////////////////////////////////////////////// SELECT SystemTitle.Value AS OurCompany, BalanceSections.Division, BalanceSections.Section, BalanceSections.SectionOrder, GeneralJournalSummary.Account.AccountType AS AccountType, GeneralJournalSummary.Account AS Account, GeneralJournalSummary.FiscalYear AS FiscalYear, GeneralJournalSummary.DayPeriod AS DayPeriod, GeneralJournalSummary.WeekPeriod AS WeekPeriod, GeneralJournalSummary.MonthPeriod AS MonthPeriod, GeneralJournalSummary.QuarterPeriod AS QuarterPeriod, GeneralJournalSummary.YearPeriod AS YearPeriod, GeneralJournalSummary.AmountRCClosingBalance AS AmountRCBalance, GeneralJournalSummary.AmountRCOpeningBalance AS AmountRCOpeningBalance, GeneralJournalSummary.AmountRCTurnover AS AmountRCTurnover, GeneralJournalSummary.YearOpeningBalance, GeneralJournalSummary.YearClosingBalance, GeneralJournalSummary.Account.RetainedEarnings AS RetainedEarnings, RetainedEarningsAccount.Account AS RetainedEarningsAccount, CASE WHEN GeneralJournalSummary.Account.RetainedEarnings THEN GeneralJournalSummary.AmountRCOpeningBalance ELSE 0 END AS RetainedEarningsOpeningBalance, CASE WHEN GeneralJournalSummary.Account.RetainedEarnings THEN GeneralJournalSummary.AmountRCClosingBalance ELSE 0 END AS RetainedEarningsClosingBalance, CASE WHEN NOT GeneralJournalSummary.Account.RetainedEarnings THEN GeneralJournalSummary.YearOpeningBalance ELSE 0 END AS RetainedEarningsYearOpeningBalance, CASE WHEN NOT GeneralJournalSummary.Account.RetainedEarnings THEN GeneralJournalSummary.YearClosingBalance ELSE 0 END AS RetainedEarningsYearClosingBalance INTO GeneralJournalTotal FROM BalanceSections AS BalanceSections LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE) LEFT JOIN GeneralJournalSummary AS GeneralJournalSummary ON (GeneralJournalSummary.Section = BalanceSections.Section) LEFT JOIN RetainedEarningsAccount AS RetainedEarningsAccount ON (TRUE) ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED GeneralJournalTotal.OurCompany, GeneralJournalTotal.Division, GeneralJournalTotal.Section, GeneralJournalTotal.SectionOrder, GeneralJournalTotal.AccountType, GeneralJournalTotal.Account, GeneralJournalTotal.FiscalYear, GeneralJournalTotal.DayPeriod, GeneralJournalTotal.WeekPeriod, GeneralJournalTotal.MonthPeriod, GeneralJournalTotal.QuarterPeriod, GeneralJournalTotal.YearPeriod, GeneralJournalTotal.AmountRCBalance, GeneralJournalTotal.AmountRCOpeningBalance, GeneralJournalTotal.AmountRCTurnover, GeneralJournalTotal.YearOpeningBalance, GeneralJournalTotal.YearClosingBalance, GeneralJournalTotal.RetainedEarnings, GeneralJournalTotal.RetainedEarningsAccount, GeneralJournalTotal.RetainedEarningsOpeningBalance, GeneralJournalTotal.RetainedEarningsClosingBalance, GeneralJournalTotal.RetainedEarningsYearOpeningBalance, GeneralJournalTotal.RetainedEarningsYearClosingBalance INTO AllAccounts FROM GeneralJournalTotal AS GeneralJournalTotal {WHERE (GeneralJournalTotal.DayPeriod BETWEEN &BeginOfPeriod AND &EndOfPeriod)}
GROUP BY GeneralJournalTotal.OurCompany, GeneralJournalTotal.Division, GeneralJournalTotal.Section, GeneralJournalTotal.SectionOrder, GeneralJournalTotal.AccountType, GeneralJournalTotal.Account, GeneralJournalTotal.FiscalYear, GeneralJournalTotal.DayPeriod, GeneralJournalTotal.WeekPeriod, GeneralJournalTotal.MonthPeriod, GeneralJournalTotal.QuarterPeriod, GeneralJournalTotal.YearPeriod, GeneralJournalTotal.AmountRCBalance, GeneralJournalTotal.AmountRCOpeningBalance, GeneralJournalTotal.AmountRCTurnover, GeneralJournalTotal.YearOpeningBalance, GeneralJournalTotal.YearClosingBalance, GeneralJournalTotal.RetainedEarnings, GeneralJournalTotal.RetainedEarningsAccount, GeneralJournalTotal.RetainedEarningsOpeningBalance, GeneralJournalTotal.RetainedEarningsClosingBalance, GeneralJournalTotal.RetainedEarningsYearOpeningBalance, GeneralJournalTotal.RetainedEarningsYearClosingBalance ;
//////////////////////////////////////////////////////////////////////////////// SELECT AllAccounts.OurCompany, AllAccounts.Division, AllAccounts.Section, AllAccounts.SectionOrder, VALUE(Enum.AccountTypes.Equity) AS AccountType, AllAccounts.RetainedEarningsAccount AS Account, AllAccounts.FiscalYear, AllAccounts.DayPeriod, AllAccounts.WeekPeriod, AllAccounts.MonthPeriod, AllAccounts.QuarterPeriod, AllAccounts.YearPeriod, CASE WHEN AllAccounts.AccountType = VALUE(Enum.AccountTypes.Equity) THEN AllAccounts.AmountRCBalance ELSE 0 END AS AmountRCBalance, CASE WHEN AllAccounts.AccountType = VALUE(Enum.AccountTypes.Equity) THEN AllAccounts.AmountRCOpeningBalance ELSE 0 END AS AmountRCOpeningBalance, AllAccounts.AmountRCTurnover, AllAccounts.YearOpeningBalance, AllAccounts.YearClosingBalance, TRUE AS RetainedEarnings, AllAccounts.RetainedEarningsAccount, AllAccounts.RetainedEarningsOpeningBalance, AllAccounts.RetainedEarningsClosingBalance, AllAccounts.RetainedEarningsYearOpeningBalance, AllAccounts.RetainedEarningsYearClosingBalance INTO RE_A FROM AllAccounts AS AllAccounts WHERE (AllAccounts.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.IncomeTaxExpense)) OR AllAccounts.AccountType = VALUE(Enum.AccountTypes.Equity) AND AllAccounts.RetainedEarnings = TRUE) ;
//////////////////////////////////////////////////////////////////////////////// SELECT RE.OurCompany, RE.Division AS Division, RE.Section, RE.SectionOrder AS SectionOrder, RE.AccountType, RE.Account, RE.FiscalYear, RE.DayPeriod AS DayPeriod, RE.WeekPeriod, RE.MonthPeriod, RE.QuarterPeriod, RE.YearPeriod, RE.RetainedEarnings, RE.RetainedEarningsAccount, SUM(RE.AmountRCOpeningBalance) AS AmountRCOpeningBalance, SUM(RE.YearOpeningBalance) AS YearOpeningBalance, SUM(RE.RetainedEarningsOpeningBalance) AS RetainedEarningsOpeningBalance, SUM(RE.RetainedEarningsYearOpeningBalance) AS RetainedEarningsYearOpeningBalance, SUM(RE.AmountRCTurnover) AS AmountRCTurnover, SUM(RE.AmountRCBalance - RE.AmountRCOpeningBalance) AS AmountRCTurnoverBalance, SUM(RE.YearClosingBalance - RE.YearOpeningBalance) AS YearTurnoverBalance, SUM(RE.RetainedEarningsClosingBalance - RE.RetainedEarningsOpeningBalance) AS RetainedEarningsTurnoverBalance, SUM(RE.RetainedEarningsYearClosingBalance - RE.RetainedEarningsYearOpeningBalance) AS RetainedEarningsYearTurnoverBalance INTO RE_B FROM RE_A AS RE
GROUP BY RE.OurCompany, RE.Division, RE.Section, RE.SectionOrder, RE.AccountType, RE.Account, RE.FiscalYear, RE.DayPeriod, RE.WeekPeriod, RE.MonthPeriod, RE.QuarterPeriod, RE.YearPeriod, RE.RetainedEarnings, RE.RetainedEarningsAccount
INDEX BY DayPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT MIN(RE_B.DayPeriod) AS MinDayPeriod INTO RE_MIN FROM RE_B AS RE_B ;
//////////////////////////////////////////////////////////////////////////////// SELECT RE_MIN.MinDayPeriod AS MinDayPeriod, ISNULL(RE_B.AmountRCOpeningBalance, 0) AS AmountRCOpeningBalance, ISNULL(RE_B.YearOpeningBalance, 0) AS YearOpeningBalance, ISNULL(RE_B.RetainedEarningsOpeningBalance, 0) AS RetainedEarningsOpeningBalance, ISNULL(RE_B.RetainedEarningsYearOpeningBalance, 0) AS RetainedEarningsYearOpeningBalance INTO RE_OpeningBalance FROM RE_MIN AS RE_MIN LEFT JOIN RE_B AS RE_B ON (RE_B.DayPeriod = RE_MIN.MinDayPeriod) ;
//////////////////////////////////////////////////////////////////////////////// SELECT RE_B1.OurCompany, RE_B1.Division, RE_B1.Section, RE_B1.SectionOrder, RE_B1.AccountType, RE_B1.Account, RE_B1.FiscalYear, RE_B1.DayPeriod, RE_B1.WeekPeriod, RE_B1.MonthPeriod, RE_B1.QuarterPeriod, RE_B1.YearPeriod, RE_B1.RetainedEarnings, RE_B1.RetainedEarningsAccount, RE_B1.AmountRCTurnover, SUM(CASE WHEN RE_B1.DayPeriod > RE_B2.DayPeriod THEN ISNULL(RE_B2.AmountRCTurnoverBalance, 0) ELSE 0 END) + MAX(ISNULL(RE_OpeningBalance.AmountRCOpeningBalance, 0)) AS AmountRCOpeningBalance, SUM(ISNULL(RE_B2.AmountRCTurnoverBalance, 0)) + MAX(ISNULL(RE_OpeningBalance.AmountRCOpeningBalance, 0)) AS AmountRCBalance, SUM(CASE WHEN RE_B1.DayPeriod > RE_B2.DayPeriod THEN ISNULL(RE_B2.YearTurnoverBalance, 0) ELSE 0 END) + MAX(ISNULL(RE_OpeningBalance.YearOpeningBalance, 0)) AS YearOpeningBalance, SUM(ISNULL(RE_B2.YearTurnoverBalance, 0)) + MAX(ISNULL(RE_OpeningBalance.YearOpeningBalance, 0)) AS YearClosingBalance, SUM(CASE WHEN RE_B1.DayPeriod > RE_B2.DayPeriod THEN ISNULL(RE_B2.RetainedEarningsTurnoverBalance, 0) ELSE 0 END) + MAX(ISNULL(RE_OpeningBalance.RetainedEarningsOpeningBalance, 0)) AS RetainedEarningsOpeningBalance, SUM(ISNULL(RE_B2.RetainedEarningsTurnoverBalance, 0)) + MAX(ISNULL(RE_OpeningBalance.RetainedEarningsOpeningBalance, 0)) AS RetainedEarningsClosingBalance, SUM(CASE WHEN RE_B1.DayPeriod > RE_B2.DayPeriod THEN ISNULL(RE_B2.RetainedEarningsYearTurnoverBalance, 0) ELSE 0 END) + MAX(ISNULL(RE_OpeningBalance.RetainedEarningsYearOpeningBalance, 0)) AS RetainedEarningsYearOpeningBalance, SUM(ISNULL(RE_B2.RetainedEarningsYearTurnoverBalance, 0)) + MAX(ISNULL(RE_OpeningBalance.RetainedEarningsYearOpeningBalance, 0)) AS RetainedEarningsYearClosingBalance INTO RE_C FROM RE_B AS RE_B1 LEFT JOIN RE_B AS RE_B2 ON RE_B1.DayPeriod >= RE_B2.DayPeriod LEFT JOIN RE_OpeningBalance AS RE_OpeningBalance ON (TRUE)
GROUP BY RE_B1.OurCompany, RE_B1.Division, RE_B1.Section, RE_B1.SectionOrder, RE_B1.AccountType, RE_B1.Account, RE_B1.FiscalYear, RE_B1.DayPeriod, RE_B1.WeekPeriod, RE_B1.MonthPeriod, RE_B1.QuarterPeriod, RE_B1.YearPeriod, RE_B1.RetainedEarnings, RE_B1.RetainedEarningsAccount, RE_B1.AmountRCTurnover ;
//////////////////////////////////////////////////////////////////////////////// SELECT RE_C.OurCompany, RE_C.Division, RE_C.Section, RE_C.SectionOrder, RE_C.AccountType, RE_C.Account, RE_C.FiscalYear, RE_C.DayPeriod, RE_C.WeekPeriod, RE_C.MonthPeriod, RE_C.QuarterPeriod, RE_C.YearPeriod, RE_C.AmountRCOpeningBalance AS AmountRCOpeningBalance, RE_C.AmountRCBalance AS AmountRCBalance, RE_C.AmountRCTurnover, RE_C.YearOpeningBalance AS YearOpeningBalance, RE_C.YearClosingBalance AS YearClosingBalance, RE_C.RetainedEarnings, RE_C.RetainedEarningsAccount, RE_C.RetainedEarningsOpeningBalance AS RetainedEarningsOpeningBalance, RE_C.RetainedEarningsClosingBalance AS RetainedEarningsClosingBalance, RE_C.RetainedEarningsYearOpeningBalance AS RetainedEarningsYearOpeningBalance, RE_C.RetainedEarningsYearClosingBalance AS RetainedEarningsYearClosingBalance FROM RE_C AS RE_C
UNION ALL
SELECT AllAccounts.OurCompany, AllAccounts.Division, AllAccounts.Section, AllAccounts.SectionOrder, AllAccounts.AccountType, AllAccounts.Account, AllAccounts.FiscalYear, AllAccounts.DayPeriod, AllAccounts.WeekPeriod, AllAccounts.MonthPeriod, AllAccounts.QuarterPeriod, AllAccounts.YearPeriod, AllAccounts.AmountRCOpeningBalance, AllAccounts.AmountRCBalance, AllAccounts.AmountRCTurnover, AllAccounts.YearOpeningBalance, AllAccounts.YearClosingBalance, FALSE, AllAccounts.RetainedEarningsAccount, AllAccounts.RetainedEarningsOpeningBalance, AllAccounts.RetainedEarningsClosingBalance, AllAccounts.RetainedEarningsYearOpeningBalance, AllAccounts.RetainedEarningsYearClosingBalance FROM AllAccounts AS AllAccounts WHERE CASE WHEN AllAccounts.RetainedEarnings THEN FALSE ELSE TRUE END
