dataSet.query:
SELECT BankReconciliation.BankAccount AS BankAccount, MIN(BankReconciliation.Date) AS DatePeriod INTO BegBal FROM Document.BankReconciliation AS BankReconciliation WHERE BEGINOFPERIOD(BankReconciliation.Date, MONTH) <= &EndOfPeriod AND BankReconciliation.Posted = TRUE AND BankReconciliation.DeletionMark = FALSE
GROUP BY BankReconciliation.BankAccount ;
//////////////////////////////////////////////////////////////////////////////// SELECT ChartOfAccounts.Ref AS BankAccount, DATETIME(1, 1, 1) AS MonthPeriod, 0 AS OpeningBalance, ISNULL(BankReconciliation.BeginningBalance, 0) AS ClosingBalance INTO BankReconciliationBalanceAndTurnovers FROM ChartOfAccounts.ChartOfAccounts AS ChartOfAccounts LEFT JOIN BegBal AS BegBal LEFT JOIN Document.BankReconciliation AS BankReconciliation ON BegBal.BankAccount = BankReconciliation.BankAccount AND BegBal.DatePeriod = BankReconciliation.Date ON ChartOfAccounts.Ref = BegBal.BankAccount WHERE (ChartOfAccounts.AccountType = VALUE(Enum.AccountTypes.Bank) OR ChartOfAccounts.AccountType = VALUE(Enum.AccountTypes.OtherCurrentLiability) AND ChartOfAccounts.CreditCard = TRUE)
UNION ALL
SELECT BankReconciliation.BankAccount, BEGINOFPERIOD(BankReconciliation.Date, MONTH), BankReconciliation.BeginningBalance, BankReconciliation.EndingBalance FROM Document.BankReconciliation AS BankReconciliation WHERE BEGINOFPERIOD(BankReconciliation.Date, MONTH) <= &EndOfPeriod AND BankReconciliation.Posted = TRUE AND BankReconciliation.DeletionMark = FALSE
INDEX BY BankAccount, MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, BankReconciliationBalanceAndTurnovers.MonthPeriod AS MonthPeriod, BankReconciliationBalanceAndTurnovers.OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance INTO BankReconciliationBalanceAndTurnoversMiddle FROM BankReconciliationBalanceAndTurnovers AS BankReconciliationBalanceAndTurnovers WHERE BankReconciliationBalanceAndTurnovers.MonthPeriod >= &BeginOfPeriod
INDEX BY MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, MAX(BankReconciliationBalanceAndTurnovers.MonthPeriod) AS MonthPeriod INTO BankReconciliationBalanceAndTurnoversMin FROM BankReconciliationBalanceAndTurnovers AS BankReconciliationBalanceAndTurnovers WHERE BankReconciliationBalanceAndTurnovers.MonthPeriod < &BeginOfPeriod
GROUP BY BankReconciliationBalanceAndTurnovers.BankAccount ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount AS BankAccount, BEGINOFPERIOD(&BeginOfPeriod, MONTH) AS MonthPeriod, BankReconciliationBalanceAndTurnovers.ClosingBalance AS OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance AS ClosingBalance INTO BankReconciliationBalanceAndTurnoversTop FROM BankReconciliationBalanceAndTurnoversMin AS BankReconciliationBalanceAndTurnoversMin LEFT JOIN BankReconciliationBalanceAndTurnovers AS BankReconciliationBalanceAndTurnovers ON BankReconciliationBalanceAndTurnoversMin.BankAccount = BankReconciliationBalanceAndTurnovers.BankAccount AND BankReconciliationBalanceAndTurnoversMin.MonthPeriod = BankReconciliationBalanceAndTurnovers.MonthPeriod WHERE NOT BEGINOFPERIOD(&BeginOfPeriod, MONTH) IN (SELECT BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod FROM BankReconciliationBalanceAndTurnoversMiddle AS BankReconciliationBalanceAndTurnoversMiddle) ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, MAX(BankReconciliationBalanceAndTurnovers.MonthPeriod) AS MonthPeriod INTO BankReconciliationBalanceAndTurnoversMax FROM BankReconciliationBalanceAndTurnovers AS BankReconciliationBalanceAndTurnovers
GROUP BY BankReconciliationBalanceAndTurnovers.BankAccount ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount AS BankAccount, BEGINOFPERIOD(&EndOfPeriod, MONTH) AS MonthPeriod, BankReconciliationBalanceAndTurnovers.ClosingBalance AS OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance AS ClosingBalance INTO BankReconciliationBalanceAndTurnoversBottom FROM BankReconciliationBalanceAndTurnoversMax AS BankReconciliationBalanceAndTurnoversMax LEFT JOIN BankReconciliationBalanceAndTurnovers AS BankReconciliationBalanceAndTurnovers ON BankReconciliationBalanceAndTurnoversMax.BankAccount = BankReconciliationBalanceAndTurnovers.BankAccount AND BankReconciliationBalanceAndTurnoversMax.MonthPeriod = BankReconciliationBalanceAndTurnovers.MonthPeriod WHERE NOT BEGINOFPERIOD(&EndOfPeriod, MONTH) IN (SELECT BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod FROM BankReconciliationBalanceAndTurnoversMiddle AS BankReconciliationBalanceAndTurnoversMiddle) ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnoversTop.BankAccount AS Account, BankReconciliationBalanceAndTurnoversTop.BankAccount.AccountType AS AccountType, BankReconciliationBalanceAndTurnoversTop.MonthPeriod AS MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversTop.MonthPeriod, QUARTER) AS QuarterPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversTop.MonthPeriod, YEAR) AS YearPeriod, BankReconciliationBalanceAndTurnoversTop.ClosingBalance AS AmountRCOpeningBalance, BankReconciliationBalanceAndTurnoversTop.ClosingBalance AS AmountRCClosingBalance INTO BankReconciliationBalanceAndTurnoversTotal FROM BankReconciliationBalanceAndTurnoversTop AS BankReconciliationBalanceAndTurnoversTop
UNION
SELECT BankReconciliationBalanceAndTurnoversMiddle.BankAccount, BankReconciliationBalanceAndTurnoversMiddle.BankAccount.AccountType, BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, QUARTER), BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, YEAR), BankReconciliationBalanceAndTurnoversMiddle.OpeningBalance, BankReconciliationBalanceAndTurnoversMiddle.ClosingBalance FROM BankReconciliationBalanceAndTurnoversMiddle AS BankReconciliationBalanceAndTurnoversMiddle
UNION
SELECT BankReconciliationBalanceAndTurnoversBottom.BankAccount, BankReconciliationBalanceAndTurnoversBottom.BankAccount.AccountType, BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, QUARTER), BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, YEAR), BankReconciliationBalanceAndTurnoversBottom.ClosingBalance, BankReconciliationBalanceAndTurnoversBottom.ClosingBalance FROM BankReconciliationBalanceAndTurnoversBottom AS BankReconciliationBalanceAndTurnoversBottom
INDEX BY Account, MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalTurnovers.Account AS Account, GeneralJournalTurnovers.DayPeriod AS DayPeriod, GeneralJournalTurnovers.WeekPeriod AS WeekPeriod, GeneralJournalTurnovers.MonthPeriod AS MonthPeriod, GeneralJournalTurnovers.QuarterPeriod AS QuarterPeriod, GeneralJournalTurnovers.YearPeriod AS YearPeriod, GeneralJournalTurnovers.Recorder AS Recorder, GeneralJournalTurnovers.LineNumber AS LineNumber, CASE WHEN GeneralJournalTurnovers.Recorder REFS Document.GeneralJournalEntry AND GeneralJournalTurnovers.Recorder.Adjusting = TRUE THEN 0 ELSE GeneralJournalTurnovers.AmountRCTurnover END AS AmountRCTurnover INTO GeneralJournalTurnoversTT FROM AccountingRegister.GeneralJournal.Turnovers( , , Auto, Account.AccountType = VALUE(Enum.AccountTypes.Bank) OR Account.AccountType = VALUE(Enum.AccountTypes.OtherCurrentLiability) AND Account.CreditCard = TRUE, , ) AS GeneralJournalTurnovers ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankBalance.Account AS Account, NULL AS DayPeriod, NULL AS WeekPeriod, BankBalance.MonthPeriod AS MonthPeriod, BankBalance.QuarterPeriod AS QuarterPeriod, BankBalance.YearPeriod AS YearPeriod, NULL AS Recorder, NULL AS LineNumber, 0 AS AmountRCTurnover INTO BankA FROM BankReconciliationBalanceAndTurnoversTotal AS BankBalance
UNION ALL
SELECT BankTurnovers.Account, BankTurnovers.DayPeriod, BankTurnovers.WeekPeriod, BankTurnovers.MonthPeriod, BankTurnovers.QuarterPeriod, BankTurnovers.YearPeriod, BankTurnovers.Recorder, BankTurnovers.LineNumber, BankTurnovers.AmountRCTurnover FROM GeneralJournalTurnoversTT AS BankTurnovers ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankA.Account, BankA.DayPeriod, BankA.WeekPeriod, BankA.MonthPeriod, BankA.QuarterPeriod, BankA.YearPeriod, BankA.Recorder, BankA.LineNumber, BankA.AmountRCTurnover, MAX(BankReconciliationBalanceAndTurnoversTotal.MonthPeriod) AS MonthPeriodMax INTO BankB FROM BankA AS BankA LEFT JOIN BankReconciliationBalanceAndTurnoversTotal AS BankReconciliationBalanceAndTurnoversTotal ON BankA.Account = BankReconciliationBalanceAndTurnoversTotal.Account AND BankA.MonthPeriod >= BankReconciliationBalanceAndTurnoversTotal.MonthPeriod
GROUP BY BankA.Account, BankA.DayPeriod, BankA.WeekPeriod, BankA.MonthPeriod, BankA.QuarterPeriod, BankA.YearPeriod, BankA.Recorder, BankA.LineNumber, BankA.AmountRCTurnover ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankB.Account AS Account, BankB.Account.AccountType AS AccountType, BankB.DayPeriod AS DayPeriod, BankB.WeekPeriod AS WeekPeriod, BankB.MonthPeriod AS MonthPeriod, BankB.QuarterPeriod AS QuarterPeriod, BankB.YearPeriod AS YearPeriod, BankB.Recorder AS Recorder, BankB.LineNumber AS LineNumber, ISNULL(CASE WHEN BankB.MonthPeriod > BankB.MonthPeriodMax THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance ELSE BankReconciliationBalanceAndTurnoversTotal.AmountRCOpeningBalance END, 0) AS AmountRCOpeningBalance, ISNULL(CASE WHEN BankB.LineNumber IS NULL THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance WHEN BankB.MonthPeriod > BankB.MonthPeriodMax THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance ELSE BankReconciliationBalanceAndTurnoversTotal.AmountRCOpeningBalance END, 0) AS AmountRCClosingBalance, BankB.AmountRCTurnover AS AmountRCTurnover INTO BankTotal FROM BankB AS BankB LEFT JOIN BankReconciliationBalanceAndTurnoversTotal AS BankReconciliationBalanceAndTurnoversTotal ON BankB.Account = BankReconciliationBalanceAndTurnoversTotal.Account AND BankB.MonthPeriodMax = BankReconciliationBalanceAndTurnoversTotal.MonthPeriod
INDEX BY AccountType ;
//////////////////////////////////////////////////////////////////////////////// SELECT CashFlowDataTurnovers.Account AS Account, CashFlowDataTurnovers.Account.AccountType AS AccountType, CashFlowDataTurnovers.DayPeriod AS DayPeriod, CashFlowDataTurnovers.WeekPeriod AS WeekPeriod, CashFlowDataTurnovers.MonthPeriod AS MonthPeriod, CashFlowDataTurnovers.QuarterPeriod AS QuarterPeriod, CashFlowDataTurnovers.YearPeriod AS YearPeriod, CashFlowDataTurnovers.Recorder AS Recorder, CashFlowDataTurnovers.LineNumber AS LineNumber, CASE WHEN CashFlowDataTurnovers.Recorder REFS Document.InvoicePayment OR CashFlowDataTurnovers.Recorder REFS Document.CashReceipt OR CashFlowDataTurnovers.Recorder REFS Document.Deposit AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.Check AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.CashSale AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.OtherIncome)) AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.IncomeTaxExpense)) AND CashFlowDataTurnovers.AmountRCExpense <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover ELSE 0 END AS AmountRCTurnover INTO CashFlowDataTurnoversTT FROM AccumulationRegister.CashFlowData.Turnovers( , , Auto, Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) OR Account = VALUE(ChartOfAccounts.ChartOfAccounts.EmptyRef)) AS CashFlowDataTurnovers WHERE NOT CashFlowDataTurnovers.Recorder REFS Document.PurchaseInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesReturn
INDEX BY Account, AccountType ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED SystemTitle.Value AS OurCompany, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN VALUE(Enum.AccountTypes.OtherExpense) ELSE ISNULL(AccountTypes.Ref, VALUE(Enum.AccountTypes.Income)) END AS AccountType, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Income) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherIncome) OR AccountTypes.Ref IS NULL THEN "RECEIPTS" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.CostOfSales) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.Expense) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherExpense) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN "DISBURSEMENTS" ELSE "" END AS AccountTypePart, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Income) OR AccountTypes.Ref IS NULL THEN "INCOME" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.CostOfSales) THEN "COST OF SALES" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Expense) THEN "OPERATING EXPENSES" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherIncome) THEN "OTHER INCOME" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherExpense) THEN "NON-OPERATING EXPENSES" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN "NON-OPERATING EXPENSES" ELSE "" END AS AccountTypePresentation, CashFlowDataTurnoversTT.Account AS Account, CashFlowDataTurnoversTT.DayPeriod AS DayPeriod, CashFlowDataTurnoversTT.WeekPeriod AS WeekPeriod, CashFlowDataTurnoversTT.MonthPeriod AS MonthPeriod, CashFlowDataTurnoversTT.QuarterPeriod AS QuarterPeriod, CashFlowDataTurnoversTT.YearPeriod AS YearPeriod, CashFlowDataTurnoversTT.Recorder AS Recorder, CashFlowDataTurnoversTT.LineNumber AS LineNumber, ISNULL(CashFlowDataTurnoversTT.AmountRCTurnover, 0) AS AmountRCTurnover, 0 AS AmountRCOpeningBalance, 0 AS AmountRCClosingBalance, "" AS TextHeader, "" AS TextFooter, "" AS TextTitle {SELECT AccountType.*, Account.*, LineNumber, Recorder, YearPeriod, QuarterPeriod, MonthPeriod, WeekPeriod, DayPeriod} FROM CashFlowDataTurnoversTT AS CashFlowDataTurnoversTT FULL JOIN Enum.AccountTypes AS AccountTypes ON (AccountTypes.Ref = CashFlowDataTurnoversTT.AccountType) LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE) WHERE (AccountTypes.Ref IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) OR CashFlowDataTurnoversTT.Account = VALUE(ChartOfAccounts.ChartOfAccounts.EmptyRef)) {WHERE AccountTypes.Ref AS AccountType, CashFlowDataTurnoversTT.Account AS Account, CashFlowDataTurnoversTT.LineNumber AS LineNumber, CashFlowDataTurnoversTT.Recorder AS Recorder, CashFlowDataTurnoversTT.YearPeriod AS YearPeriod, CashFlowDataTurnoversTT.QuarterPeriod AS QuarterPeriod, CashFlowDataTurnoversTT.MonthPeriod AS MonthPeriod, CashFlowDataTurnoversTT.WeekPeriod AS WeekPeriod, CashFlowDataTurnoversTT.DayPeriod AS DayPeriod}
UNION ALL
SELECT SystemTitle.Value, AccountTypes.Ref, "BANK", "Bank", BankTotal.Account, BankTotal.DayPeriod, BankTotal.WeekPeriod, BankTotal.MonthPeriod, BankTotal.QuarterPeriod, BankTotal.YearPeriod, BankTotal.Recorder, 0, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCTurnover ELSE 0 END, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCOpeningBalance ELSE 0 END, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCClosingBalance ELSE 0 END, "", "", "" FROM BankTotal AS BankTotal FULL JOIN Enum.AccountTypes AS AccountTypes ON (AccountTypes.Ref = BankTotal.AccountType) LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE) WHERE AccountTypes.Ref IN (VALUE(Enum.AccountTypes.Bank), VALUE(Enum.AccountTypes.OtherCurrentLiability)) {WHERE AccountTypes.Ref AS AccountType, BankTotal.Account AS Account, BankTotal.LineNumber AS LineNumber, BankTotal.Recorder AS Recorder, BankTotal.YearPeriod AS YearPeriod, BankTotal.QuarterPeriod AS QuarterPeriod, BankTotal.MonthPeriod AS MonthPeriod, BankTotal.WeekPeriod AS WeekPeriod, BankTotal.DayPeriod AS DayPeriod}
