dataSet.query:
SELECT BankReconciliation.BankAccount AS BankAccount, BEGINOFPERIOD(BankReconciliation.Date, MONTH) AS DateMonth, BankReconciliation.BeginningBalance AS BB, BankReconciliation.EndingBalance AS CB INTO DocumentsBankReconciliation FROM Document.BankReconciliation AS BankReconciliation WHERE BEGINOFPERIOD(BankReconciliation.Date, MONTH) <= &EndOfPeriod AND BankReconciliation.Posted = TRUE AND BankReconciliation.DeletionMark = FALSE
INDEX BY BankAccount, DateMonth ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliation.BankAccount AS BankAccount, MIN(BankReconciliation.DateMonth) AS DatePeriod INTO BegBal FROM DocumentsBankReconciliation AS BankReconciliation
GROUP BY BankReconciliation.BankAccount
INDEX BY BankAccount, DatePeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT BegBal.BankAccount AS BankAccount, DATETIME(1, 1, 1) AS MonthPeriod, 0 AS OpeningBalance, ISNULL(BankReconciliation.BB, 0) AS ClosingBalance INTO BankAccountsBB FROM BegBal AS BegBal LEFT JOIN DocumentsBankReconciliation AS BankReconciliation ON BegBal.BankAccount = BankReconciliation.BankAccount AND BegBal.DatePeriod = BankReconciliation.DateMonth ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankAccountsBB.BankAccount AS BankAccount, BankAccountsBB.MonthPeriod AS MonthPeriod, BankAccountsBB.OpeningBalance AS OpeningBalance, BankAccountsBB.ClosingBalance AS ClosingBalance INTO BankReconciliationBBAndTurnovers FROM BankAccountsBB AS BankAccountsBB
UNION ALL
SELECT BankReconciliation.BankAccount, BankReconciliation.DateMonth, BankReconciliation.BB, BankReconciliation.CB FROM DocumentsBankReconciliation AS BankReconciliation
INDEX BY BankAccount, MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, BankReconciliationBalanceAndTurnovers.MonthPeriod AS MonthPeriod, BankReconciliationBalanceAndTurnovers.OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance INTO BankReconciliationBalanceAndTurnoversMiddle FROM BankReconciliationBBAndTurnovers AS BankReconciliationBalanceAndTurnovers WHERE BankReconciliationBalanceAndTurnovers.MonthPeriod >= &BeginOfPeriod
INDEX BY MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, MAX(BankReconciliationBalanceAndTurnovers.MonthPeriod) AS MonthPeriod INTO BankReconciliationBalanceAndTurnoversMin FROM BankReconciliationBBAndTurnovers AS BankReconciliationBalanceAndTurnovers WHERE BankReconciliationBalanceAndTurnovers.MonthPeriod < &BeginOfPeriod
GROUP BY BankReconciliationBalanceAndTurnovers.BankAccount ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount AS BankAccount, BEGINOFPERIOD(&BeginOfPeriod, MONTH) AS MonthPeriod, BankReconciliationBalanceAndTurnovers.ClosingBalance AS OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance AS ClosingBalance INTO BankReconciliationBalanceAndTurnoversTop FROM BankReconciliationBalanceAndTurnoversMin AS BankReconciliationBalanceAndTurnoversMin LEFT JOIN BankReconciliationBBAndTurnovers AS BankReconciliationBalanceAndTurnovers ON BankReconciliationBalanceAndTurnoversMin.BankAccount = BankReconciliationBalanceAndTurnovers.BankAccount AND BankReconciliationBalanceAndTurnoversMin.MonthPeriod = BankReconciliationBalanceAndTurnovers.MonthPeriod WHERE NOT (BEGINOFPERIOD(&BeginOfPeriod, MONTH), BankReconciliationBalanceAndTurnoversMin.BankAccount) IN (SELECT BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, BankReconciliationBalanceAndTurnoversMiddle.BankAccount FROM BankReconciliationBalanceAndTurnoversMiddle AS BankReconciliationBalanceAndTurnoversMiddle) ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, MAX(BankReconciliationBalanceAndTurnovers.MonthPeriod) AS MonthPeriod INTO BankReconciliationBalanceAndTurnoversMax FROM BankReconciliationBBAndTurnovers AS BankReconciliationBalanceAndTurnovers
GROUP BY BankReconciliationBalanceAndTurnovers.BankAccount ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount AS BankAccount, BEGINOFPERIOD(&EndOfPeriod, MONTH) AS MonthPeriod, BankReconciliationBalanceAndTurnovers.ClosingBalance AS OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance AS ClosingBalance INTO BankReconciliationBalanceAndTurnoversBottom FROM BankReconciliationBalanceAndTurnoversMax AS BankReconciliationBalanceAndTurnoversMax LEFT JOIN BankReconciliationBBAndTurnovers AS BankReconciliationBalanceAndTurnovers ON BankReconciliationBalanceAndTurnoversMax.BankAccount = BankReconciliationBalanceAndTurnovers.BankAccount AND BankReconciliationBalanceAndTurnoversMax.MonthPeriod = BankReconciliationBalanceAndTurnovers.MonthPeriod WHERE NOT (BEGINOFPERIOD(&EndOfPeriod, MONTH), BankReconciliationBalanceAndTurnoversMax.BankAccount) IN (SELECT BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, BankReconciliationBalanceAndTurnoversMiddle.BankAccount FROM BankReconciliationBalanceAndTurnoversMiddle AS BankReconciliationBalanceAndTurnoversMiddle) ;
//////////////////////////////////////////////////////////////////////////////// SELECT DISTINCT BankReconciliationBalanceAndTurnoversMiddle.BankAccount AS BankAccount INTO BankAccounts FROM BankReconciliationBalanceAndTurnoversMiddle AS BankReconciliationBalanceAndTurnoversMiddle WHERE (BankReconciliationBalanceAndTurnoversMiddle.BankAccount.AccountType = VALUE(Enum.AccountTypes.Bank) OR BankReconciliationBalanceAndTurnoversMiddle.BankAccount.CreditCard)
UNION
SELECT DISTINCT BankReconciliationBalanceAndTurnoversTop.BankAccount FROM BankReconciliationBalanceAndTurnoversTop AS BankReconciliationBalanceAndTurnoversTop WHERE (BankReconciliationBalanceAndTurnoversTop.BankAccount.AccountType = VALUE(Enum.AccountTypes.Bank) OR BankReconciliationBalanceAndTurnoversTop.BankAccount.CreditCard) AND BankReconciliationBalanceAndTurnoversTop.OpeningBalance <> 0 ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnoversTop.BankAccount AS Account, BankReconciliationBalanceAndTurnoversTop.MonthPeriod AS MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversTop.MonthPeriod, QUARTER) AS QuarterPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversTop.MonthPeriod, YEAR) AS YearPeriod, BankReconciliationBalanceAndTurnoversTop.ClosingBalance AS AmountRCOpeningBalance, BankReconciliationBalanceAndTurnoversTop.ClosingBalance AS AmountRCClosingBalance INTO BankReconciliationBalanceAndTurnoversTotal FROM BankReconciliationBalanceAndTurnoversTop AS BankReconciliationBalanceAndTurnoversTop WHERE BankReconciliationBalanceAndTurnoversTop.BankAccount IN (SELECT BankAccounts.BankAccount AS BankAccount FROM BankAccounts AS BankAccounts)
UNION
SELECT BankReconciliationBalanceAndTurnoversMiddle.BankAccount, BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, QUARTER), BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, YEAR), BankReconciliationBalanceAndTurnoversMiddle.OpeningBalance, BankReconciliationBalanceAndTurnoversMiddle.ClosingBalance FROM BankReconciliationBalanceAndTurnoversMiddle AS BankReconciliationBalanceAndTurnoversMiddle WHERE BankReconciliationBalanceAndTurnoversMiddle.BankAccount IN (SELECT BankAccounts.BankAccount AS BankAccount FROM BankAccounts AS BankAccounts)
UNION
SELECT BankReconciliationBalanceAndTurnoversBottom.BankAccount, BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, QUARTER), BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, YEAR), BankReconciliationBalanceAndTurnoversBottom.ClosingBalance, BankReconciliationBalanceAndTurnoversBottom.ClosingBalance FROM BankReconciliationBalanceAndTurnoversBottom AS BankReconciliationBalanceAndTurnoversBottom WHERE BankReconciliationBalanceAndTurnoversBottom.BankAccount IN (SELECT BankAccounts.BankAccount AS BankAccount FROM BankAccounts AS BankAccounts)
INDEX BY Account, MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalTurnovers.Account AS Account, GeneralJournalTurnovers.DayPeriod AS DayPeriod, GeneralJournalTurnovers.WeekPeriod AS WeekPeriod, GeneralJournalTurnovers.MonthPeriod AS MonthPeriod, GeneralJournalTurnovers.QuarterPeriod AS QuarterPeriod, GeneralJournalTurnovers.YearPeriod AS YearPeriod, GeneralJournalTurnovers.Recorder AS Recorder, GeneralJournalTurnovers.LineNumber AS LineNumber, CASE WHEN GeneralJournalTurnovers.Recorder REFS Document.GeneralJournalEntry AND GeneralJournalTurnovers.Recorder.Adjusting = TRUE THEN 0 ELSE GeneralJournalTurnovers.AmountRCTurnover END AS AmountRCTurnover INTO GeneralJournalTurnoversTT FROM AccountingRegister.GeneralJournal.Turnovers( {(&BeginOfPeriod)}, {(&EndOfPeriod)}, Auto, Account IN (SELECT BankAccounts.BankAccount AS Account FROM BankAccounts AS BankAccounts), , ) AS GeneralJournalTurnovers ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankBalance.Account AS Account, NULL AS DayPeriod, NULL AS WeekPeriod, BankBalance.MonthPeriod AS MonthPeriod, BankBalance.QuarterPeriod AS QuarterPeriod, BankBalance.YearPeriod AS YearPeriod, NULL AS Recorder, NULL AS LineNumber, 0 AS AmountRCTurnover INTO BankA FROM BankReconciliationBalanceAndTurnoversTotal AS BankBalance
UNION ALL
SELECT BankTurnovers.Account, BankTurnovers.DayPeriod, BankTurnovers.WeekPeriod, BankTurnovers.MonthPeriod, BankTurnovers.QuarterPeriod, BankTurnovers.YearPeriod, BankTurnovers.Recorder, BankTurnovers.LineNumber, BankTurnovers.AmountRCTurnover FROM GeneralJournalTurnoversTT AS BankTurnovers ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankA.Account, BankA.DayPeriod, BankA.WeekPeriod, BankA.MonthPeriod, BankA.QuarterPeriod, BankA.YearPeriod, BankA.Recorder, BankA.LineNumber, BankA.AmountRCTurnover, MAX(BankReconciliationBalanceAndTurnoversTotal.MonthPeriod) AS MonthPeriodMax INTO BankB FROM BankA AS BankA LEFT JOIN BankReconciliationBalanceAndTurnoversTotal AS BankReconciliationBalanceAndTurnoversTotal ON BankA.Account = BankReconciliationBalanceAndTurnoversTotal.Account AND BankA.MonthPeriod >= BankReconciliationBalanceAndTurnoversTotal.MonthPeriod
GROUP BY BankA.Account, BankA.DayPeriod, BankA.WeekPeriod, BankA.MonthPeriod, BankA.QuarterPeriod, BankA.YearPeriod, BankA.Recorder, BankA.LineNumber, BankA.AmountRCTurnover ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankB.Account AS Account, BankB.DayPeriod AS DayPeriod, BankB.WeekPeriod AS WeekPeriod, BankB.MonthPeriod AS MonthPeriod, BankB.QuarterPeriod AS QuarterPeriod, BankB.YearPeriod AS YearPeriod, BankB.Recorder AS Recorder, BankB.LineNumber AS LineNumber, ISNULL(CASE WHEN BankB.MonthPeriod > BankB.MonthPeriodMax THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance ELSE BankReconciliationBalanceAndTurnoversTotal.AmountRCOpeningBalance END, 0) AS AmountRCOpeningBalance, ISNULL(CASE WHEN BankB.LineNumber IS NULL THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance WHEN BankB.MonthPeriod > BankB.MonthPeriodMax THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance ELSE BankReconciliationBalanceAndTurnoversTotal.AmountRCOpeningBalance END, 0) AS AmountRCClosingBalance, BankB.AmountRCTurnover AS AmountRCTurnover INTO BankTotal FROM BankB AS BankB LEFT JOIN BankReconciliationBalanceAndTurnoversTotal AS BankReconciliationBalanceAndTurnoversTotal ON BankB.Account = BankReconciliationBalanceAndTurnoversTotal.Account AND BankB.MonthPeriodMax = BankReconciliationBalanceAndTurnoversTotal.MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT CashFlowDataTurnovers.Account AS Account, CashFlowDataTurnovers.DayPeriod AS DayPeriod, CashFlowDataTurnovers.WeekPeriod AS WeekPeriod, CashFlowDataTurnovers.MonthPeriod AS MonthPeriod, CashFlowDataTurnovers.QuarterPeriod AS QuarterPeriod, CashFlowDataTurnovers.YearPeriod AS YearPeriod, CashFlowDataTurnovers.Recorder AS Recorder, CashFlowDataTurnovers.LineNumber AS LineNumber, CASE WHEN CashFlowDataTurnovers.Recorder REFS Document.InvoicePayment OR CashFlowDataTurnovers.Recorder REFS Document.CashReceipt OR CashFlowDataTurnovers.Recorder REFS Document.Deposit AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.Check AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.CashSale AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.OtherIncome)) AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.IncomeTaxExpense)) AND CashFlowDataTurnovers.AmountRCExpense <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover ELSE 0 END AS AmountRCTurnover INTO CashFlowDataTurnoversTT FROM AccumulationRegister.CashFlowData.Turnovers( {(&BeginOfPeriod)}, {(&EndOfPeriod)}, Auto, Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) OR Account = VALUE(ChartOfAccounts.ChartOfAccounts.EmptyRef)) AS CashFlowDataTurnovers WHERE NOT CashFlowDataTurnovers.Recorder REFS Document.PurchaseInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesReturn
INDEX BY Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankAccountsBB.BankAccount AS BankAccount, BankAccountsBB.MonthPeriod AS MonthPeriod, BankAccountsBB.OpeningBalance AS OpeningBalance, BankAccountsBB.ClosingBalance AS ClosingBalance INTO BankReconciliationBBAndTurnoversPP FROM BankAccountsBB AS BankAccountsBB
UNION ALL
SELECT BankReconciliation.BankAccount, BankReconciliation.DateMonth, BankReconciliation.BB, BankReconciliation.CB FROM DocumentsBankReconciliation AS BankReconciliation WHERE BankReconciliation.DateMonth <= &EndOfPeriodPP
INDEX BY BankAccount, MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, BankReconciliationBalanceAndTurnovers.MonthPeriod AS MonthPeriod, BankReconciliationBalanceAndTurnovers.OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance INTO BankReconciliationBalanceAndTurnoversMiddlePP FROM BankReconciliationBBAndTurnoversPP AS BankReconciliationBalanceAndTurnovers WHERE BankReconciliationBalanceAndTurnovers.MonthPeriod >= &BeginOfPeriodPP
INDEX BY MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, MAX(BankReconciliationBalanceAndTurnovers.MonthPeriod) AS MonthPeriod INTO BankReconciliationBalanceAndTurnoversMinPP FROM BankReconciliationBBAndTurnoversPP AS BankReconciliationBalanceAndTurnovers WHERE BankReconciliationBalanceAndTurnovers.MonthPeriod < &BeginOfPeriodPP
GROUP BY BankReconciliationBalanceAndTurnovers.BankAccount ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount AS BankAccount, BEGINOFPERIOD(&BeginOfPeriodPP, MONTH) AS MonthPeriod, BankReconciliationBalanceAndTurnovers.ClosingBalance AS OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance AS ClosingBalance INTO BankReconciliationBalanceAndTurnoversTopPP FROM BankReconciliationBalanceAndTurnoversMinPP AS BankReconciliationBalanceAndTurnoversMin LEFT JOIN BankReconciliationBBAndTurnoversPP AS BankReconciliationBalanceAndTurnovers ON BankReconciliationBalanceAndTurnoversMin.BankAccount = BankReconciliationBalanceAndTurnovers.BankAccount AND BankReconciliationBalanceAndTurnoversMin.MonthPeriod = BankReconciliationBalanceAndTurnovers.MonthPeriod WHERE NOT (BEGINOFPERIOD(&BeginOfPeriodPP, MONTH), BankReconciliationBalanceAndTurnoversMin.BankAccount) IN (SELECT BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, BankReconciliationBalanceAndTurnoversMiddle.BankAccount FROM BankReconciliationBalanceAndTurnoversMiddlePP AS BankReconciliationBalanceAndTurnoversMiddle) ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount, MAX(BankReconciliationBalanceAndTurnovers.MonthPeriod) AS MonthPeriod INTO BankReconciliationBalanceAndTurnoversMaxPP FROM BankReconciliationBBAndTurnoversPP AS BankReconciliationBalanceAndTurnovers
GROUP BY BankReconciliationBalanceAndTurnovers.BankAccount ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnovers.BankAccount AS BankAccount, BEGINOFPERIOD(&EndOfPeriodPP, MONTH) AS MonthPeriod, BankReconciliationBalanceAndTurnovers.ClosingBalance AS OpeningBalance, BankReconciliationBalanceAndTurnovers.ClosingBalance AS ClosingBalance INTO BankReconciliationBalanceAndTurnoversBottomPP FROM BankReconciliationBalanceAndTurnoversMaxPP AS BankReconciliationBalanceAndTurnoversMax LEFT JOIN BankReconciliationBBAndTurnoversPP AS BankReconciliationBalanceAndTurnovers ON BankReconciliationBalanceAndTurnoversMax.BankAccount = BankReconciliationBalanceAndTurnovers.BankAccount AND BankReconciliationBalanceAndTurnoversMax.MonthPeriod = BankReconciliationBalanceAndTurnovers.MonthPeriod WHERE NOT (BEGINOFPERIOD(&EndOfPeriodPP, MONTH), BankReconciliationBalanceAndTurnoversMax.BankAccount) IN (SELECT BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, BankReconciliationBalanceAndTurnoversMiddle.BankAccount FROM BankReconciliationBalanceAndTurnoversMiddlePP AS BankReconciliationBalanceAndTurnoversMiddle) ;
//////////////////////////////////////////////////////////////////////////////// SELECT DISTINCT BankReconciliationBalanceAndTurnoversMiddle.BankAccount AS BankAccount INTO BankAccountsPP FROM BankReconciliationBalanceAndTurnoversMiddlePP AS BankReconciliationBalanceAndTurnoversMiddle WHERE (BankReconciliationBalanceAndTurnoversMiddle.BankAccount.AccountType = VALUE(Enum.AccountTypes.Bank) OR BankReconciliationBalanceAndTurnoversMiddle.BankAccount.CreditCard)
UNION
SELECT DISTINCT BankReconciliationBalanceAndTurnoversTop.BankAccount FROM BankReconciliationBalanceAndTurnoversTopPP AS BankReconciliationBalanceAndTurnoversTop WHERE (BankReconciliationBalanceAndTurnoversTop.BankAccount.AccountType = VALUE(Enum.AccountTypes.Bank) OR BankReconciliationBalanceAndTurnoversTop.BankAccount.CreditCard) AND BankReconciliationBalanceAndTurnoversTop.OpeningBalance <> 0 ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankReconciliationBalanceAndTurnoversTop.BankAccount AS Account, BankReconciliationBalanceAndTurnoversTop.MonthPeriod AS MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversTop.MonthPeriod, QUARTER) AS QuarterPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversTop.MonthPeriod, YEAR) AS YearPeriod, BankReconciliationBalanceAndTurnoversTop.ClosingBalance AS AmountRCOpeningBalance, BankReconciliationBalanceAndTurnoversTop.ClosingBalance AS AmountRCClosingBalance INTO BankReconciliationBalanceAndTurnoversTotalPP FROM BankReconciliationBalanceAndTurnoversTopPP AS BankReconciliationBalanceAndTurnoversTop WHERE BankReconciliationBalanceAndTurnoversTop.BankAccount IN (SELECT BankAccounts.BankAccount AS BankAccount FROM BankAccountsPP AS BankAccounts)
UNION
SELECT BankReconciliationBalanceAndTurnoversMiddle.BankAccount, BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, QUARTER), BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversMiddle.MonthPeriod, YEAR), BankReconciliationBalanceAndTurnoversMiddle.OpeningBalance, BankReconciliationBalanceAndTurnoversMiddle.ClosingBalance FROM BankReconciliationBalanceAndTurnoversMiddlePP AS BankReconciliationBalanceAndTurnoversMiddle WHERE BankReconciliationBalanceAndTurnoversMiddle.BankAccount IN (SELECT BankAccounts.BankAccount AS BankAccount FROM BankAccountsPP AS BankAccounts)
UNION
SELECT BankReconciliationBalanceAndTurnoversBottom.BankAccount, BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, QUARTER), BEGINOFPERIOD(BankReconciliationBalanceAndTurnoversBottom.MonthPeriod, YEAR), BankReconciliationBalanceAndTurnoversBottom.ClosingBalance, BankReconciliationBalanceAndTurnoversBottom.ClosingBalance FROM BankReconciliationBalanceAndTurnoversBottomPP AS BankReconciliationBalanceAndTurnoversBottom WHERE BankReconciliationBalanceAndTurnoversBottom.BankAccount IN (SELECT BankAccounts.BankAccount AS BankAccount FROM BankAccountsPP AS BankAccounts)
INDEX BY Account, MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalTurnovers.Account AS Account, GeneralJournalTurnovers.DayPeriod AS DayPeriod, GeneralJournalTurnovers.WeekPeriod AS WeekPeriod, GeneralJournalTurnovers.MonthPeriod AS MonthPeriod, GeneralJournalTurnovers.QuarterPeriod AS QuarterPeriod, GeneralJournalTurnovers.YearPeriod AS YearPeriod, GeneralJournalTurnovers.Recorder AS Recorder, GeneralJournalTurnovers.LineNumber AS LineNumber, CASE WHEN GeneralJournalTurnovers.Recorder REFS Document.GeneralJournalEntry AND GeneralJournalTurnovers.Recorder.Adjusting = TRUE THEN 0 ELSE GeneralJournalTurnovers.AmountRCTurnover END AS AmountRCTurnover INTO GeneralJournalTurnoversTTPP FROM AccountingRegister.GeneralJournal.Turnovers( {(&BeginOfPeriodPP)}, {(&EndOfPeriodPP)}, Auto, Account IN (SELECT BankAccounts.BankAccount AS Account FROM BankAccounts AS BankAccounts), , ) AS GeneralJournalTurnovers ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankBalance.Account AS Account, NULL AS DayPeriod, NULL AS WeekPeriod, BankBalance.MonthPeriod AS MonthPeriod, BankBalance.QuarterPeriod AS QuarterPeriod, BankBalance.YearPeriod AS YearPeriod, NULL AS Recorder, NULL AS LineNumber, 0 AS AmountRCTurnover INTO BankAPP FROM BankReconciliationBalanceAndTurnoversTotalPP AS BankBalance
UNION ALL
SELECT BankTurnovers.Account, BankTurnovers.DayPeriod, BankTurnovers.WeekPeriod, BankTurnovers.MonthPeriod, BankTurnovers.QuarterPeriod, BankTurnovers.YearPeriod, BankTurnovers.Recorder, BankTurnovers.LineNumber, BankTurnovers.AmountRCTurnover FROM GeneralJournalTurnoversTTPP AS BankTurnovers ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankA.Account, BankA.DayPeriod, BankA.WeekPeriod, BankA.MonthPeriod, BankA.QuarterPeriod, BankA.YearPeriod, BankA.Recorder, BankA.LineNumber, BankA.AmountRCTurnover, MAX(BankReconciliationBalanceAndTurnoversTotal.MonthPeriod) AS MonthPeriodMax INTO BankBPP FROM BankAPP AS BankA LEFT JOIN BankReconciliationBalanceAndTurnoversTotalPP AS BankReconciliationBalanceAndTurnoversTotal ON BankA.Account = BankReconciliationBalanceAndTurnoversTotal.Account AND BankA.MonthPeriod >= BankReconciliationBalanceAndTurnoversTotal.MonthPeriod
GROUP BY BankA.Account, BankA.DayPeriod, BankA.WeekPeriod, BankA.MonthPeriod, BankA.QuarterPeriod, BankA.YearPeriod, BankA.Recorder, BankA.LineNumber, BankA.AmountRCTurnover ;
//////////////////////////////////////////////////////////////////////////////// SELECT BankB.Account AS Account, BankB.DayPeriod AS DayPeriod, BankB.WeekPeriod AS WeekPeriod, BankB.MonthPeriod AS MonthPeriod, BankB.QuarterPeriod AS QuarterPeriod, BankB.YearPeriod AS YearPeriod, BankB.Recorder AS Recorder, BankB.LineNumber AS LineNumber, ISNULL(CASE WHEN BankB.MonthPeriod > BankB.MonthPeriodMax THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance ELSE BankReconciliationBalanceAndTurnoversTotal.AmountRCOpeningBalance END, 0) AS AmountRCOpeningBalance, ISNULL(CASE WHEN BankB.LineNumber IS NULL THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance WHEN BankB.MonthPeriod > BankB.MonthPeriodMax THEN BankReconciliationBalanceAndTurnoversTotal.AmountRCClosingBalance ELSE BankReconciliationBalanceAndTurnoversTotal.AmountRCOpeningBalance END, 0) AS AmountRCClosingBalance, BankB.AmountRCTurnover AS AmountRCTurnover INTO BankTotalPP FROM BankBPP AS BankB LEFT JOIN BankReconciliationBalanceAndTurnoversTotalPP AS BankReconciliationBalanceAndTurnoversTotal ON BankB.Account = BankReconciliationBalanceAndTurnoversTotal.Account AND BankB.MonthPeriodMax = BankReconciliationBalanceAndTurnoversTotal.MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT CashFlowDataTurnovers.Account AS Account, CashFlowDataTurnovers.DayPeriod AS DayPeriod, CashFlowDataTurnovers.WeekPeriod AS WeekPeriod, CashFlowDataTurnovers.MonthPeriod AS MonthPeriod, CashFlowDataTurnovers.QuarterPeriod AS QuarterPeriod, CashFlowDataTurnovers.YearPeriod AS YearPeriod, CashFlowDataTurnovers.Recorder AS Recorder, CashFlowDataTurnovers.LineNumber AS LineNumber, CASE WHEN CashFlowDataTurnovers.Recorder REFS Document.InvoicePayment OR CashFlowDataTurnovers.Recorder REFS Document.CashReceipt OR CashFlowDataTurnovers.Recorder REFS Document.Deposit AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.Check AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.CashSale AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.OtherIncome)) AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.IncomeTaxExpense)) AND CashFlowDataTurnovers.AmountRCExpense <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover ELSE 0 END AS AmountRCTurnover INTO CashFlowDataTurnoversTTPP FROM AccumulationRegister.CashFlowData.Turnovers( {(&BeginOfPeriodPP)}, {(&EndOfPeriodPP)}, Auto, Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) OR Account = VALUE(ChartOfAccounts.ChartOfAccounts.EmptyRef)) AS CashFlowDataTurnovers WHERE NOT CashFlowDataTurnovers.Recorder REFS Document.PurchaseInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesReturn
INDEX BY Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT CashFlowDataTurnoversTT.Account AS Account, CashFlowDataTurnoversTT.Account.AccountType AS AccountType, CashFlowDataTurnoversTT.DayPeriod AS DayPeriod, CashFlowDataTurnoversTT.WeekPeriod AS WeekPeriod, CashFlowDataTurnoversTT.MonthPeriod AS MonthPeriod, CashFlowDataTurnoversTT.QuarterPeriod AS QuarterPeriod, CashFlowDataTurnoversTT.YearPeriod AS YearPeriod, CashFlowDataTurnoversTT.Recorder AS Recorder, CashFlowDataTurnoversTT.LineNumber AS LineNumber, ISNULL(CashFlowDataTurnoversTT.AmountRCTurnover, 0) AS AmountRCTurnover, 0 AS AmountRCOpeningBalance, 0 AS AmountRCClosingBalance, NULL AS AmountRCTurnoverPP, NULL AS AmountRCOpeningBalancePP, NULL AS AmountRCClosingBalancePP, 1 AS SpecialOrder INTO TemporaryTotal FROM CashFlowDataTurnoversTT AS CashFlowDataTurnoversTT
UNION ALL
SELECT BankTotal.Account, VALUE(Enum.AccountTypes.Bank), BankTotal.DayPeriod, BankTotal.WeekPeriod, BankTotal.MonthPeriod, BankTotal.QuarterPeriod, BankTotal.YearPeriod, BankTotal.Recorder, 0, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCTurnover ELSE 0 END, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCOpeningBalance ELSE 0 END, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCClosingBalance ELSE 0 END, NULL, NULL, NULL, CASE WHEN BankTotal.Recorder IS NULL THEN 3 ELSE 1 END FROM BankTotal AS BankTotal
UNION ALL
SELECT CashFlowDataTurnoversTT.Account, CashFlowDataTurnoversTT.Account.AccountType, DATEADD(CashFlowDataTurnoversTT.DayPeriod, YEAR, 1), DATEADD(CashFlowDataTurnoversTT.WeekPeriod, YEAR, 1), DATEADD(CashFlowDataTurnoversTT.MonthPeriod, YEAR, 1), DATEADD(CashFlowDataTurnoversTT.QuarterPeriod, YEAR, 1), DATEADD(CashFlowDataTurnoversTT.YearPeriod, YEAR, 1), CashFlowDataTurnoversTT.Recorder, CashFlowDataTurnoversTT.LineNumber, NULL, NULL, NULL, ISNULL(CashFlowDataTurnoversTT.AmountRCTurnover, 0), 0, 0, 1 FROM CashFlowDataTurnoversTTPP AS CashFlowDataTurnoversTT
UNION ALL
SELECT BankTotal.Account, VALUE(Enum.AccountTypes.Bank), DATEADD(BankTotal.DayPeriod, YEAR, 1), DATEADD(BankTotal.WeekPeriod, YEAR, 1), DATEADD(BankTotal.MonthPeriod, YEAR, 1), DATEADD(BankTotal.QuarterPeriod, YEAR, 1), DATEADD(BankTotal.YearPeriod, YEAR, 1), BankTotal.Recorder, 0, NULL, NULL, NULL, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCTurnover ELSE 0 END, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCOpeningBalance ELSE 0 END, CASE WHEN NOT BankTotal.Account IS NULL THEN BankTotal.AmountRCClosingBalance ELSE 0 END, CASE WHEN BankTotal.Recorder IS NULL THEN 2 ELSE 1 END FROM BankTotalPP AS BankTotal ;
//////////////////////////////////////////////////////////////////////////////// SELECT TemporaryTotal.Account, TemporaryTotal.AccountType, TemporaryTotal.DayPeriod, TemporaryTotal.WeekPeriod, TemporaryTotal.MonthPeriod, TemporaryTotal.QuarterPeriod, TemporaryTotal.YearPeriod, TemporaryTotal.SpecialOrder, TemporaryTotal.Recorder, TemporaryTotal.LineNumber, TemporaryTotal.AmountRCTurnover, TemporaryTotal.AmountRCOpeningBalance, TemporaryTotal.AmountRCClosingBalance, TemporaryTotal.AmountRCTurnoverPP, TemporaryTotal.AmountRCOpeningBalancePP, TemporaryTotal.AmountRCClosingBalancePP, MAX(BankReconciliationBBAndTurnovers.MonthPeriod) AS BalanceMonth INTO FullTotal FROM TemporaryTotal AS TemporaryTotal LEFT JOIN BankReconciliationBBAndTurnovers AS BankReconciliationBBAndTurnovers ON TemporaryTotal.Account = BankReconciliationBBAndTurnovers.BankAccount AND TemporaryTotal.MonthPeriod > BankReconciliationBBAndTurnovers.MonthPeriod
GROUP BY TemporaryTotal.MonthPeriod, TemporaryTotal.WeekPeriod, TemporaryTotal.YearPeriod, TemporaryTotal.Account, TemporaryTotal.AccountType, TemporaryTotal.DayPeriod, TemporaryTotal.Recorder, TemporaryTotal.QuarterPeriod, TemporaryTotal.LineNumber, TemporaryTotal.AmountRCTurnover, TemporaryTotal.AmountRCOpeningBalance, TemporaryTotal.AmountRCClosingBalance, TemporaryTotal.AmountRCTurnoverPP, TemporaryTotal.AmountRCOpeningBalancePP, TemporaryTotal.AmountRCClosingBalancePP, TemporaryTotal.SpecialOrder ;
//////////////////////////////////////////////////////////////////////////////// SELECT FullTotal.Account, FullTotal.AccountType, FullTotal.DayPeriod, FullTotal.WeekPeriod, FullTotal.MonthPeriod, FullTotal.QuarterPeriod, FullTotal.YearPeriod, FullTotal.SpecialOrder, FullTotal.Recorder, FullTotal.LineNumber, ISNULL(FullTotal.AmountRCTurnover, 0) AS AmountRCTurnover, ISNULL(FullTotal.AmountRCOpeningBalance, BankReconciliationBBAndTurnovers.ClosingBalance) AS AmountRCOpeningBalance, ISNULL(FullTotal.AmountRCClosingBalance, BankReconciliationBBAndTurnovers.ClosingBalance) AS AmountRCClosingBalance, FullTotal.AmountRCTurnoverPP, FullTotal.AmountRCOpeningBalancePP, FullTotal.AmountRCClosingBalancePP, FullTotal.BalanceMonth INTO FullTotalPP FROM FullTotal AS FullTotal LEFT JOIN BankReconciliationBBAndTurnovers AS BankReconciliationBBAndTurnovers ON FullTotal.Account = BankReconciliationBBAndTurnovers.BankAccount AND FullTotal.BalanceMonth = BankReconciliationBBAndTurnovers.MonthPeriod ;
//////////////////////////////////////////////////////////////////////////////// SELECT CashFlowDataTurnovers.Account AS Account, CashFlowDataTurnovers.Account.AccountType AS AccountType, CashFlowDataTurnovers.Recorder AS Recorder, CashFlowDataTurnovers.LineNumber AS LineNumber, CASE WHEN CashFlowDataTurnovers.Recorder REFS Document.InvoicePayment OR CashFlowDataTurnovers.Recorder REFS Document.CashReceipt OR CashFlowDataTurnovers.Recorder REFS Document.Deposit AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.Check AND CashFlowDataTurnovers.AmountRCExpense <> 0 OR CashFlowDataTurnovers.Recorder REFS Document.CashSale AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.OtherIncome)) AND CashFlowDataTurnovers.AmountRCReceipt <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover WHEN CashFlowDataTurnovers.Recorder REFS Document.GeneralJournalEntry AND CashFlowDataTurnovers.Recorder.Adjusting = FALSE AND CashFlowDataTurnovers.Account.AccountType IN (VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.IncomeTaxExpense)) AND CashFlowDataTurnovers.AmountRCExpense <> 0 THEN CashFlowDataTurnovers.AmountRCTurnover ELSE 0 END AS AmountRCTurnover INTO CashFlowDataTurnoversFP FROM AccumulationRegister.CashFlowData.Turnovers( {(&BeginOfFiscalYear)}, {(&EndOfPeriod)}, Record, Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) OR Account = VALUE(ChartOfAccounts.ChartOfAccounts.EmptyRef)) AS CashFlowDataTurnovers WHERE NOT CashFlowDataTurnovers.Recorder REFS Document.PurchaseInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesInvoice AND NOT CashFlowDataTurnovers.Recorder REFS Document.SalesReturn ;
//////////////////////////////////////////////////////////////////////////////// SELECT FullTotalPP.Account, FullTotalPP.AccountType, FullTotalPP.DayPeriod, FullTotalPP.WeekPeriod, FullTotalPP.MonthPeriod, FullTotalPP.QuarterPeriod, FullTotalPP.YearPeriod, FullTotalPP.SpecialOrder, FullTotalPP.Recorder, FullTotalPP.LineNumber, FullTotalPP.AmountRCTurnover, FullTotalPP.AmountRCOpeningBalance, FullTotalPP.AmountRCClosingBalance, FullTotalPP.AmountRCTurnoverPP, FullTotalPP.AmountRCOpeningBalancePP, FullTotalPP.AmountRCClosingBalancePP, FullTotalPP.BalanceMonth, MAX(BankReconciliationBBAndTurnoversPP.MonthPeriod) AS BalanceMonthPP, NULL AS AmountRCTurnoverFP INTO Total FROM FullTotalPP AS FullTotalPP LEFT JOIN BankReconciliationBBAndTurnoversPP AS BankReconciliationBBAndTurnoversPP ON FullTotalPP.Account = BankReconciliationBBAndTurnoversPP.BankAccount AND (CASE WHEN FullTotalPP.SpecialOrder = 3 THEN DATEADD(FullTotalPP.MonthPeriod, YEAR, -1) >= BankReconciliationBBAndTurnoversPP.MonthPeriod ELSE DATEADD(FullTotalPP.MonthPeriod, YEAR, -1) > BankReconciliationBBAndTurnoversPP.MonthPeriod END)
GROUP BY FullTotalPP.WeekPeriod, FullTotalPP.BalanceMonth, FullTotalPP.Recorder, FullTotalPP.Account, FullTotalPP.AccountType, FullTotalPP.DayPeriod, FullTotalPP.MonthPeriod, FullTotalPP.QuarterPeriod, FullTotalPP.YearPeriod, FullTotalPP.SpecialOrder, FullTotalPP.LineNumber, FullTotalPP.AmountRCTurnover, FullTotalPP.AmountRCOpeningBalance, FullTotalPP.AmountRCClosingBalance, FullTotalPP.AmountRCTurnoverPP, FullTotalPP.AmountRCOpeningBalancePP, FullTotalPP.AmountRCClosingBalancePP
UNION ALL
SELECT CashFlowDataTurnoversFP.Account, CashFlowDataTurnoversFP.AccountType, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, CashFlowDataTurnoversFP.AmountRCTurnover FROM CashFlowDataTurnoversFP AS CashFlowDataTurnoversFP ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED SystemTitle.Value AS OurCompany, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN VALUE(Enum.AccountTypes.OtherExpense) ELSE ISNULL(AccountTypes.Ref, VALUE(Enum.AccountTypes.Income)) END AS AccountType, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Income) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherIncome) OR AccountTypes.Ref IS NULL THEN "RECEIPTS" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.CostOfSales) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.Expense) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherExpense) OR AccountTypes.Ref = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN "DISBURSEMENTS" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Bank) THEN "BANK" ELSE "" END AS AccountTypePart, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Income) OR AccountTypes.Ref IS NULL THEN "INCOME" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.CostOfSales) THEN "COST OF SALES" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Expense) THEN "OPERATING EXPENSES" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherIncome) THEN "OTHER INCOME" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherExpense) THEN "NON-OPERATING EXPENSES" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN "NON-OPERATING EXPENSES" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Bank) THEN "Bank" ELSE "" END AS AccountTypePresentation, Total.Account AS Account, Total.Account.CashFromOperations AS CashFromOperations, Total.DayPeriod AS DayPeriod, Total.WeekPeriod AS WeekPeriod, Total.MonthPeriod AS MonthPeriod, Total.QuarterPeriod AS QuarterPeriod, Total.YearPeriod AS YearPeriod, Total.SpecialOrder AS SpecialOrder, Total.Recorder AS Recorder, Total.LineNumber AS LineNumber, Total.AmountRCTurnover, Total.AmountRCOpeningBalance, Total.AmountRCClosingBalance, ISNULL(Total.AmountRCTurnoverPP, 0) AS AmountRCTurnoverPP, ISNULL(Total.AmountRCOpeningBalancePP, BankReconciliationBBAndTurnoversPP.ClosingBalance) AS AmountRCOpeningBalancePP, ISNULL(Total.AmountRCClosingBalancePP, BankReconciliationBBAndTurnoversPP.ClosingBalance) AS AmountRCClosingBalancePP, "" AS TextHeader, "" AS TextFooter, "Schedule of Receipts and Disbursements" AS TextTitle, Total.BalanceMonth, Total.BalanceMonthPP, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Bank) THEN Total.Account WHEN Total.Recorder REFS Document.Deposit THEN CAST(Total.Recorder AS Document.Deposit).BankAccount WHEN Total.Recorder REFS Document.Check THEN CAST(Total.Recorder AS Document.Check).BankAccount ELSE VALUE(ChartOfAccounts.ChartOfAccounts.EmptyRef) END AS BankAccount, Total.AmountRCTurnoverFP AS AmountRCTurnoverFP {SELECT AccountType.*, Account.*, LineNumber, Recorder, SpecialOrder, YearPeriod, QuarterPeriod, MonthPeriod, WeekPeriod, DayPeriod} FROM Total AS Total LEFT JOIN BankReconciliationBBAndTurnoversPP AS BankReconciliationBBAndTurnoversPP ON Total.Account = BankReconciliationBBAndTurnoversPP.BankAccount AND Total.BalanceMonthPP = BankReconciliationBBAndTurnoversPP.MonthPeriod FULL JOIN Enum.AccountTypes AS AccountTypes ON (AccountTypes.Ref = Total.AccountType) LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE) WHERE (AccountTypes.Ref IN (VALUE(Enum.AccountTypes.Bank), VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) OR Total.Account = VALUE(ChartOfAccounts.ChartOfAccounts.EmptyRef)) {WHERE AccountTypes.Ref AS AccountType, Total.Account AS Account, Total.LineNumber AS LineNumber, Total.Recorder AS Recorder, Total.SpecialOrder AS SpecialOrder, Total.YearPeriod AS YearPeriod, Total.QuarterPeriod AS QuarterPeriod, Total.MonthPeriod AS MonthPeriod, Total.WeekPeriod AS WeekPeriod, Total.DayPeriod AS DayPeriod}
ORDER BY Total.Account, Total.MonthPeriod, Total.SpecialOrder, Total.Recorder DESC, Total.LineNumber
