dataSet.query:
SELECT GeneralJournalAnalyticsDimensionsBalance.Account, CASE WHEN GeneralJournalAnalyticsDimensionsBalance.Account.AccountType = VALUE(Enum.AccountTypes.Income) OR GeneralJournalAnalyticsDimensionsBalance.Account.AccountType = VALUE(Enum.AccountTypes.CostOfSales) OR GeneralJournalAnalyticsDimensionsBalance.Account.AccountType = VALUE(Enum.AccountTypes.Expense) OR GeneralJournalAnalyticsDimensionsBalance.Account.AccountType = VALUE(Enum.AccountTypes.OtherIncome) OR GeneralJournalAnalyticsDimensionsBalance.Account.AccountType = VALUE(Enum.AccountTypes.OtherExpense) OR GeneralJournalAnalyticsDimensionsBalance.Account.AccountType = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN GeneralJournalAnalyticsDimensionsBalance.AmountRCBalance ELSE 0 END AS AmountRCBalance INTO BeginOfFiscalYearOpeningBalance FROM AccumulationRegister.GeneralJournalAnalyticsDimensions.Balance({(&BeginOfFiscalYear)}, ) AS GeneralJournalAnalyticsDimensionsBalance ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalAnalyticsDimensionsBalance.Account, GeneralJournalAnalyticsDimensionsBalance.AmountRCBalance INTO BeginOfPeriodOpeningBalance FROM AccumulationRegister.GeneralJournalAnalyticsDimensions.Balance({(&BeginOfPeriod)}, ) AS GeneralJournalAnalyticsDimensionsBalance ;
//////////////////////////////////////////////////////////////////////////////// SELECT ISNULL(BeginOfPeriodOpeningBalance.Account, BeginOfFiscalYearOpeningBalance.Account) AS Account, ISNULL(BeginOfPeriodOpeningBalance.AmountRCBalance, 0) - ISNULL(BeginOfFiscalYearOpeningBalance.AmountRCBalance, 0) AS AmountRCBalance INTO OpeningBalance FROM BeginOfFiscalYearOpeningBalance AS BeginOfFiscalYearOpeningBalance FULL JOIN BeginOfPeriodOpeningBalance AS BeginOfPeriodOpeningBalance ON BeginOfFiscalYearOpeningBalance.Account = BeginOfPeriodOpeningBalance.Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED GeneralJournalAnalyticsDimensionsTurnovers.Account AS Account, GeneralJournalAnalyticsDimensionsTurnovers.Recorder AS Recorder, GeneralJournalAnalyticsDimensionsTurnovers.LineNumber AS LineNumber, GeneralJournalAnalyticsDimensionsTurnovers.AmountRCReceipt AS AmountRCReceipt, GeneralJournalAnalyticsDimensionsTurnovers.AmountRCExpense AS AmountRCExpense INTO Split FROM AccumulationRegister.GeneralJournalAnalyticsDimensions.Turnovers({(&BeginOfPeriod)}, {(&EndOfPeriod)}, Auto, ) AS GeneralJournalAnalyticsDimensionsTurnovers WHERE GeneralJournalAnalyticsDimensionsTurnovers.LineNumber <= 2
INDEX BY Recorder ;
//////////////////////////////////////////////////////////////////////////////// SELECT SystemTitle.Value AS OurCompany, GeneralJournalAnalyticsDimensionsTurnovers.Account AS Account, 2 AS SectionOrder, COUNT(Split.Recorder) AS RecorderOrder, GeneralJournalAnalyticsDimensionsTurnovers.Recorder AS Recorder, SUM(GeneralJournalAnalyticsDimensionsTurnovers.AmountRCTurnover) AS AmountRCTurnover, ISNULL(OpeningBalance.AmountRCBalance, 0) AS OpeningBalance, ISNULL(Split.Account, "-Split-") AS Split, CAST(GeneralJournalAnalyticsDimensionsTurnovers.Recorder.Memo AS STRING(1024)) AS RecorderMemo, GeneralJournalAnalyticsDimensionsTurnovers.Account AS AccountFilter, GeneralJournalAnalyticsDimensionsTurnovers.Account.AccountType AS AccountTypeFilter, GeneralJournalAnalyticsDimensionsTurnovers.Company AS CompanyFilter, GeneralJournalAnalyticsDimensionsTurnovers.Company.Vendor AS VendorFilter, GeneralJournalAnalyticsDimensionsTurnovers.Company.Customer AS CustomerFilter FROM AccumulationRegister.GeneralJournalAnalyticsDimensions.Turnovers({(&BeginOfPeriod)}, {(&EndOfPeriod)}, Auto, ) AS GeneralJournalAnalyticsDimensionsTurnovers LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE) LEFT JOIN OpeningBalance AS OpeningBalance ON GeneralJournalAnalyticsDimensionsTurnovers.Account = OpeningBalance.Account LEFT JOIN Split AS Split ON GeneralJournalAnalyticsDimensionsTurnovers.Recorder = Split.Recorder AND (CASE WHEN GeneralJournalAnalyticsDimensionsTurnovers.LineNumber <= 2 THEN CASE WHEN GeneralJournalAnalyticsDimensionsTurnovers.LineNumber = 1 THEN CASE WHEN Split.LineNumber = 2 THEN CASE WHEN GeneralJournalAnalyticsDimensionsTurnovers.AmountRCReceipt = Split.AmountRCExpense AND GeneralJournalAnalyticsDimensionsTurnovers.AmountRCExpense = Split.AmountRCReceipt THEN TRUE ELSE FALSE END ELSE FALSE END WHEN GeneralJournalAnalyticsDimensionsTurnovers.LineNumber = 2 THEN CASE WHEN Split.LineNumber = 1 THEN CASE WHEN GeneralJournalAnalyticsDimensionsTurnovers.AmountRCReceipt = Split.AmountRCExpense AND GeneralJournalAnalyticsDimensionsTurnovers.AmountRCExpense = Split.AmountRCReceipt THEN TRUE ELSE FALSE END ELSE FALSE END END ELSE FALSE END)
GROUP BY SystemTitle.Value, GeneralJournalAnalyticsDimensionsTurnovers.Recorder, GeneralJournalAnalyticsDimensionsTurnovers.Account, ISNULL(OpeningBalance.AmountRCBalance, 0), ISNULL(Split.Account, "-Split-"), CAST(GeneralJournalAnalyticsDimensionsTurnovers.Recorder.Memo AS STRING(1024)), GeneralJournalAnalyticsDimensionsTurnovers.Account.AccountType, GeneralJournalAnalyticsDimensionsTurnovers.Company, GeneralJournalAnalyticsDimensionsTurnovers.Company.Vendor, GeneralJournalAnalyticsDimensionsTurnovers.Company.Customer, GeneralJournalAnalyticsDimensionsTurnovers.Account
UNION ALL
SELECT SystemTitle.Value, OpeningBalance.Account, 1, 1, UNDEFINED, 0, OpeningBalance.AmountRCBalance, "", "", OpeningBalance.Account, OpeningBalance.Account.AccountType, UNDEFINED, UNDEFINED, UNDEFINED FROM OpeningBalance AS OpeningBalance LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE)
