dataSet.query:
SELECT ALLOWED CashFlowDataTurnovers.Account, CashFlowDataTurnovers.Recorder AS Recorder, CashFlowDataTurnovers.LineNumber, CashFlowDataTurnovers.AmountRCReceipt, CashFlowDataTurnovers.AmountRCExpense INTO Split FROM AccumulationRegister.CashFlowData.Turnovers(, , Auto, ) AS CashFlowDataTurnovers WHERE CashFlowDataTurnovers.LineNumber <= 2
INDEX BY Recorder ;
//////////////////////////////////////////////////////////////////////////////// SELECT ALLOWED SystemTitle.Value AS OurCompany, CashFlowDataTurnovers.Account.AccountType AS AccountType, CASE WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Income) THEN "Income" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.CostOfSales) THEN "COGS" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.Expense) THEN "Expenses" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherIncome) THEN "Other Income" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.OtherExpense) THEN "Other Expenses" WHEN AccountTypes.Ref = VALUE(Enum.AccountTypes.IncomeTaxExpense) THEN "Income Tax Expense" ELSE "" END AS AccountTypePresentation, CashFlowDataTurnovers.Account AS Account, ISNULL(Split.Account, "-Split-") AS Split, CashFlowDataTurnovers.Recorder AS Recorder, CashFlowDataTurnovers.LineNumber AS LineNumber, CASE WHEN NOT CashFlowDataTurnovers.Account IS NULL THEN -CashFlowDataTurnovers.AmountRCTurnover ELSE 0 END AS AmountRCTurnover, CASE WHEN CashFlowDataTurnovers.Company IS NULL OR CashFlowDataTurnovers.Company = VALUE(Catalog.Companies.EmptyRef) OR CashFlowDataTurnovers.Company = UNDEFINED THEN "NOT SPECIFIED" ELSE CashFlowDataTurnovers.Company END AS Company, CAST(CashFlowDataTurnovers.Recorder.Memo AS STRING(1024)) AS RecorderMemo, ISNULL(TextHeader.Text, "") AS TextHeader, ISNULL(TextFooter.Text, "") AS TextFooter, ISNULL(TextTitle.Text, "") AS TextTitle {SELECT AccountType.*, Account.*, Recorder} FROM AccumulationRegister.CashFlowData.Turnovers(, , Auto, Account.AccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense))) AS CashFlowDataTurnovers FULL JOIN Enum.AccountTypes AS AccountTypes ON (AccountTypes.Ref = CashFlowDataTurnovers.Account.AccountType) LEFT JOIN Constant.SystemTitle AS SystemTitle ON (TRUE) LEFT JOIN InformationRegister.HeadersAndFooters AS TextHeader ON (TextHeader.Report = VALUE(Enum.Reports.IncomeStatementAB)) AND (TextHeader.Type = VALUE(Enum.Partsreport.Header)) LEFT JOIN InformationRegister.HeadersAndFooters AS TextFooter ON (TextFooter.Report = VALUE(Enum.Reports.IncomeStatementAB)) AND (TextFooter.Type = VALUE(Enum.Partsreport.Footer)) LEFT JOIN InformationRegister.HeadersAndFooters AS TextTitle ON (TextTitle.Report = VALUE(Enum.Reports.IncomeStatementAB)) AND (TextTitle.Type = VALUE(Enum.Partsreport.Title)) LEFT JOIN Split AS Split ON CashFlowDataTurnovers.Recorder = Split.Recorder AND (CASE WHEN CashFlowDataTurnovers.LineNumber <= 2 THEN CASE WHEN CashFlowDataTurnovers.LineNumber = 1 THEN CASE WHEN Split.LineNumber = 2 THEN CASE WHEN CashFlowDataTurnovers.AmountRCReceipt = Split.AmountRCExpense AND CashFlowDataTurnovers.AmountRCExpense = Split.AmountRCReceipt THEN TRUE ELSE FALSE END ELSE FALSE END WHEN CashFlowDataTurnovers.LineNumber = 2 THEN CASE WHEN Split.LineNumber = 1 THEN CASE WHEN CashFlowDataTurnovers.AmountRCReceipt = Split.AmountRCExpense AND CashFlowDataTurnovers.AmountRCExpense = Split.AmountRCReceipt THEN TRUE ELSE FALSE END ELSE FALSE END END ELSE FALSE END) WHERE AccountTypes.Ref IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) {WHERE AccountTypes.Ref AS AccountType, CashFlowDataTurnovers.Account AS Account, CashFlowDataTurnovers.Recorder AS Recorder}
