dataSet.query:
SELECT TaxTurnovers.MonthPeriod AS MonthPeriod, TaxTurnovers.QuarterPeriod AS QuarterPeriod, TaxTurnovers.HalfYearPeriod AS HalfYearPeriod, TaxTurnovers.YearPeriod AS YearPeriod, TaxTurnovers.ChargeType AS ChargeType, TaxTurnovers.Agency, TaxTurnovers.TaxRate, TaxTurnovers.GrossSales AS GrossSales, TaxTurnovers.TaxableSales AS TaxableSales, TaxTurnovers.TaxAmount AS TaxAmount, TaxTurnovers.Adjustments AS Adjustments, TaxTurnovers.Payments AS Payments, TaxTurnovers.Recorder, TaxTurnovers.SalesTaxComponent AS SalesTaxComponent, TaxTurnovers.SalesTaxComponent.Rate, TaxTurnovers.SalesTaxComponent.Description, TaxTurnovers.SalesTaxComponent.Presentation, ISNULL(SalesTaxOwedDetails.ChargeDocument.Number, TaxTurnovers.Recorder.Number) AS DocumentNumber, TaxTurnovers.Recorder.Date AS DocumentDate, ISNULL(SalesTaxOwedDetails.ChargeDocument, TaxTurnovers.Recorder) AS TransactionType, CASE WHEN SalesTaxOwedDetails.ChargeDocument IS NULL THEN CASE WHEN NOT TaxTurnovers.Recorder REFS Document.SalesTaxPayment THEN TaxTurnovers.Recorder.Company ELSE VALUE(Catalog.Companies.EmptyRef) END ELSE CASE WHEN NOT SalesTaxOwedDetails.ChargeDocument REFS Document.SalesTaxPayment THEN SalesTaxOwedDetails.ChargeDocument.Company ELSE VALUE(Catalog.Companies.EmptyRef) END END AS Company, ISNULL(SalesTaxOwedDetails.ChargeDocument.Memo, TaxTurnovers.Recorder.Memo) AS Memo, ISNULL(SalesTaxOwedDetails.TaxPayable, TaxTurnovers.TaxAmount) AS TaxAmountDetail, ISNULL(SalesTaxOwedDetails.TaxableSale, TaxTurnovers.TaxableSales) AS TaxableAmountDetail, TaxTurnovers.SalesTaxComponent AS TaxName, "" AS Comment FROM (SELECT SalesTaxOwedBalanceAndTurnovers.MonthPeriod AS MonthPeriod, SalesTaxOwedBalanceAndTurnovers.QuarterPeriod AS QuarterPeriod, SalesTaxOwedBalanceAndTurnovers.HalfYearPeriod AS HalfYearPeriod, SalesTaxOwedBalanceAndTurnovers.YearPeriod AS YearPeriod, SalesTaxOwedBalanceAndTurnovers.ChargeType AS ChargeType, SalesTaxOwedBalanceAndTurnovers.Agency AS Agency, SalesTaxOwedBalanceAndTurnovers.TaxRate AS TaxRate, SalesTaxOwedBalanceAndTurnovers.SalesTaxComponent AS SalesTaxComponent, SalesTaxOwedBalanceAndTurnovers.GrossSaleReceipt AS GrossSales, SalesTaxOwedBalanceAndTurnovers.TaxableSaleReceipt AS TaxableSales, SalesTaxOwedBalanceAndTurnovers.TaxPayableReceipt AS TaxAmount, 0 AS Adjustments, SalesTaxOwedBalanceAndTurnovers.TaxPayableExpense AS Payments, SalesTaxOwedBalanceAndTurnovers.Recorder AS Recorder, SalesTaxOwedBalanceAndTurnovers.TaxPayableClosingBalance AS Balance, SalesTaxOwedBalanceAndTurnovers.TaxPayableOpeningBalance AS OpeningBalance FROM AccumulationRegister.SalesTaxOwed.BalanceAndTurnovers(, , Auto, , TaxRate <> 0) AS SalesTaxOwedBalanceAndTurnovers
UNION ALL
SELECT SalesTaxOwedBalanceAndTurnovers.MonthPeriod, SalesTaxOwedBalanceAndTurnovers.QuarterPeriod, SalesTaxOwedBalanceAndTurnovers.HalfYearPeriod, SalesTaxOwedBalanceAndTurnovers.YearPeriod, SalesTaxOwedBalanceAndTurnovers.ChargeType, SalesTaxOwedBalanceAndTurnovers.Agency, SalesTaxOwedBalanceAndTurnovers.TaxRate, SalesTaxOwedBalanceAndTurnovers.SalesTaxComponent, 0, 0, 0, SalesTaxOwedBalanceAndTurnovers.TaxPayableReceipt, SalesTaxOwedBalanceAndTurnovers.TaxPayableExpense, SalesTaxOwedBalanceAndTurnovers.Recorder, SalesTaxOwedBalanceAndTurnovers.TaxPayableClosingBalance, SalesTaxOwedBalanceAndTurnovers.TaxPayableOpeningBalance FROM AccumulationRegister.SalesTaxOwed.BalanceAndTurnovers(, , Auto, , TaxRate = 0) AS SalesTaxOwedBalanceAndTurnovers) AS TaxTurnovers LEFT JOIN InformationRegister.SalesTaxOwedDetails AS SalesTaxOwedDetails ON TaxTurnovers.Recorder = SalesTaxOwedDetails.Recorder AND (TaxTurnovers.ChargeType = VALUE(Enum.AccountingMethod.Cash)) AND TaxTurnovers.Agency = SalesTaxOwedDetails.Agency AND TaxTurnovers.TaxRate = SalesTaxOwedDetails.TaxRate AND TaxTurnovers.SalesTaxComponent = SalesTaxOwedDetails.SalesTaxComponent {WHERE TaxTurnovers.Agency.*, TaxTurnovers.TaxRate}

SELECT ALLOWED GrossSalesWithAgencies.ChargeType, GrossSalesWithAgencies.TotalGrossSales AS TotalGrossSalesPerSalesTaxComponent, GrossSalesWithAgencies.Agency, SalesTaxComponents.Ref AS SalesTaxComponent INTO GrossSalesWithSalesTaxComponents FROM (SELECT VALUE(Enum.AccountingMethod.Accrual) AS ChargeType, -1 * GeneralJournalTurnovers.AmountRCTurnover AS TotalGrossSales, SalesTaxAgencies.Ref AS Agency FROM AccountingRegister.GeneralJournal.Turnovers(, , Auto, Account.AccountType = VALUE(Enum.AccountTypes.Income), , ) AS GeneralJournalTurnovers, Catalog.SalesTaxAgencies AS SalesTaxAgencies
UNION ALL
SELECT VALUE(Enum.AccountingMethod.Cash), CASE WHEN NOT CashFlowDataTurnovers.Account IS NULL THEN -CashFlowDataTurnovers.AmountRCTurnover ELSE 0 END, SalesTaxAgencies.Ref FROM AccumulationRegister.CashFlowData.Turnovers(, , Auto, ) AS CashFlowDataTurnovers, Catalog.SalesTaxAgencies AS SalesTaxAgencies WHERE CashFlowDataTurnovers.Account.AccountType = VALUE(Enum.AccountTypes.Income)) AS GrossSalesWithAgencies INNER JOIN Catalog.SalesTaxComponents AS SalesTaxComponents ON GrossSalesWithAgencies.Agency = SalesTaxComponents.Agency ;
//////////////////////////////////////////////////////////////////////////////// SELECT GrossSalesWithSalesTaxComponents.ChargeType, SUM(GrossSalesWithSalesTaxComponents.TotalGrossSalesPerSalesTaxComponent) AS TotalGrossSalesPerSalesTaxComponent, GrossSalesWithSalesTaxComponents.Agency, GrossSalesWithSalesTaxComponents.SalesTaxComponent FROM GrossSalesWithSalesTaxComponents AS GrossSalesWithSalesTaxComponents
GROUP BY GrossSalesWithSalesTaxComponents.ChargeType, GrossSalesWithSalesTaxComponents.Agency, GrossSalesWithSalesTaxComponents.SalesTaxComponent
