dataSet.query:
SELECT TaxTurnovers.MonthPeriod, TaxTurnovers.QuarterPeriod, TaxTurnovers.HalfYearPeriod, TaxTurnovers.YearPeriod, TaxTurnovers.ChargeType, TaxTurnovers.Agency, TaxTurnovers.TaxRate, TaxTurnovers.GrossSales AS GrossSales, TaxTurnovers.TaxableSales AS TaxableSales, TaxTurnovers.TaxAmount AS TaxAmount, TaxTurnovers.Adjustments AS Adjustments, TaxTurnovers.Payments AS Payments, TaxTurnovers.Recorder, TaxTurnovers.SalesTaxComponent AS SalesTaxComponent, TaxTurnovers.SalesTaxComponent.Rate, TaxTurnovers.SalesTaxComponent.Description, TaxTurnovers.SalesTaxComponent.Presentation, ISNULL(SalesTaxOwedDetails.ChargeDocument.Number, TaxTurnovers.Recorder.Number) AS DocumentNumber, TaxTurnovers.Recorder.Date AS DocumentDate, ISNULL(SalesTaxOwedDetails.ChargeDocument, TaxTurnovers.Recorder) AS TransactionType, CASE WHEN SalesTaxOwedDetails.ChargeDocument IS NULL THEN CASE WHEN NOT TaxTurnovers.Recorder REFS Document.SalesTaxPayment THEN TaxTurnovers.Recorder.Company ELSE VALUE(Catalog.Companies.EmptyRef) END ELSE CASE WHEN NOT SalesTaxOwedDetails.ChargeDocument REFS Document.SalesTaxPayment THEN SalesTaxOwedDetails.ChargeDocument.Company ELSE VALUE(Catalog.Companies.EmptyRef) END END AS Company, ISNULL(SalesTaxOwedDetails.ChargeDocument.Memo, TaxTurnovers.Recorder.Memo) AS Memo, ISNULL(SalesTaxOwedDetails.TaxPayable, TaxTurnovers.TaxAmount) AS TaxAmountDetail, ISNULL(SalesTaxOwedDetails.TaxableSale, TaxTurnovers.TaxableSales) AS TaxableAmountDetail, TaxTurnovers.SalesTaxComponent AS TaxName FROM (SELECT SalesTaxOwedBalanceAndTurnovers.MonthPeriod AS MonthPeriod, SalesTaxOwedBalanceAndTurnovers.QuarterPeriod AS QuarterPeriod, SalesTaxOwedBalanceAndTurnovers.HalfYearPeriod AS HalfYearPeriod, SalesTaxOwedBalanceAndTurnovers.YearPeriod AS YearPeriod, SalesTaxOwedBalanceAndTurnovers.ChargeType AS ChargeType, SalesTaxOwedBalanceAndTurnovers.Agency AS Agency, SalesTaxOwedBalanceAndTurnovers.TaxRate AS TaxRate, SalesTaxOwedBalanceAndTurnovers.SalesTaxComponent AS SalesTaxComponent, SalesTaxOwedBalanceAndTurnovers.GrossSaleReceipt AS GrossSales, SalesTaxOwedBalanceAndTurnovers.TaxableSaleReceipt AS TaxableSales, SalesTaxOwedBalanceAndTurnovers.TaxPayableReceipt AS TaxAmount, 0 AS Adjustments, SalesTaxOwedBalanceAndTurnovers.TaxPayableExpense AS Payments, SalesTaxOwedBalanceAndTurnovers.Recorder AS Recorder, SalesTaxOwedBalanceAndTurnovers.TaxPayableClosingBalance AS Balance, SalesTaxOwedBalanceAndTurnovers.TaxPayableOpeningBalance AS OpeningBalance FROM AccumulationRegister.SalesTaxOwed.BalanceAndTurnovers(, , Auto, , TaxRate <> 0) AS SalesTaxOwedBalanceAndTurnovers
UNION ALL
SELECT SalesTaxOwedBalanceAndTurnovers.MonthPeriod, SalesTaxOwedBalanceAndTurnovers.QuarterPeriod, SalesTaxOwedBalanceAndTurnovers.HalfYearPeriod, SalesTaxOwedBalanceAndTurnovers.YearPeriod, SalesTaxOwedBalanceAndTurnovers.ChargeType, SalesTaxOwedBalanceAndTurnovers.Agency, SalesTaxOwedBalanceAndTurnovers.TaxRate, SalesTaxOwedBalanceAndTurnovers.SalesTaxComponent, 0, 0, 0, SalesTaxOwedBalanceAndTurnovers.TaxPayableReceipt, SalesTaxOwedBalanceAndTurnovers.TaxPayableExpense, SalesTaxOwedBalanceAndTurnovers.Recorder, SalesTaxOwedBalanceAndTurnovers.TaxPayableClosingBalance, SalesTaxOwedBalanceAndTurnovers.TaxPayableOpeningBalance FROM AccumulationRegister.SalesTaxOwed.BalanceAndTurnovers(, , Auto, , TaxRate = 0) AS SalesTaxOwedBalanceAndTurnovers) AS TaxTurnovers LEFT JOIN InformationRegister.SalesTaxOwedDetails AS SalesTaxOwedDetails ON TaxTurnovers.Recorder = SalesTaxOwedDetails.Recorder AND (TaxTurnovers.ChargeType = VALUE(Enum.AccountingMethod.Cash)) AND TaxTurnovers.Agency = SalesTaxOwedDetails.Agency AND TaxTurnovers.TaxRate = SalesTaxOwedDetails.TaxRate AND TaxTurnovers.SalesTaxComponent = SalesTaxOwedDetails.SalesTaxComponent {WHERE TaxTurnovers.Agency.*, TaxTurnovers.TaxRate}
