dataSet.query:
SELECT TOP 1 ChartOfAccounts.Ref AS Account INTO RetainedEarningsAccount FROM ChartOfAccounts.ChartOfAccounts AS ChartOfAccounts WHERE ChartOfAccounts.RetainedEarnings = TRUE ;
//////////////////////////////////////////////////////////////////////////////// SELECT GeneralJournalBalancesAndTurnovers.Account AS Account, GeneralJournalBalancesAndTurnovers.Account.AccountType AS AccountAccountType, GeneralJournalBalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr, GeneralJournalBalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr, GeneralJournalBalancesAndTurnovers.AmountRCTurnoverDr, GeneralJournalBalancesAndTurnovers.AmountRCTurnoverCr, GeneralJournalBalancesAndTurnovers.AmountRCClosingSplittedBalanceDr, GeneralJournalBalancesAndTurnovers.AmountRCClosingSplittedBalanceCr INTO BalancesAndTurnovers {SELECT Account.*} FROM AccountingRegister.GeneralJournal.BalanceAndTurnovers({(&BeginOfFiscalYear)}, {(&EndOfPeriod)}, Auto, , , , ) AS GeneralJournalBalancesAndTurnovers {WHERE GeneralJournalBalancesAndTurnovers.Account.* AS Account}
INDEX BY AccountAccountType ;
//////////////////////////////////////////////////////////////////////////////// SELECT RetainedEarningsAccount.Account AS Account, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr AS AmountRCOpeningSplittedBalanceDr, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr AS AmountRCOpeningSplittedBalanceCr, 0 AS AmountRCTurnoverDr, 0 AS AmountRCTurnoverCr, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr AS AmountRCClosingSplittedBalanceDr, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr AS AmountRCClosingSplittedBalanceCr INTO RetainedEarnings FROM BalancesAndTurnovers AS BalancesAndTurnovers LEFT JOIN RetainedEarningsAccount AS RetainedEarningsAccount ON (TRUE) WHERE BalancesAndTurnovers.AccountAccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense))
UNION ALL
SELECT RetainedEarningsAccount.Account, BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr, BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr, 0, 0, BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr, BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr FROM BalancesAndTurnovers AS BalancesAndTurnovers LEFT JOIN RetainedEarningsAccount AS RetainedEarningsAccount ON (TRUE) WHERE BalancesAndTurnovers.Account.RetainedEarnings ;
//////////////////////////////////////////////////////////////////////////////// SELECT RetainedEarnings.Account AS Account, VALUE(Enum.AccountTypes.Equity) AS AccountAccountType, RetainedEarnings.Account.Code AS AccountCode, RetainedEarnings.Account.Description AS AccountDescription, RetainedEarnings.Account.Order AS AccountOrder, TRUE AS RetainedEarnings, CASE WHEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) > 0 THEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) ELSE 0 END AS AmountRCOpeningSplittedBalanceDr, CASE WHEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) < 0 THEN -(SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr)) ELSE 0 END AS AmountRCOpeningSplittedBalanceCr, SUM(RetainedEarnings.AmountRCTurnoverDr) AS AmountRCTurnoverDr, SUM(RetainedEarnings.AmountRCTurnoverCr) AS AmountRCTurnoverCr, CASE WHEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) > 0 THEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) ELSE 0 END AS AmountRCClosingSplittedBalanceDr, CASE WHEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) < 0 THEN -(SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr)) ELSE 0 END AS AmountRCClosingSplittedBalanceCr, Constants.SystemTitle FROM RetainedEarnings AS RetainedEarnings, Constants AS Constants
GROUP BY RetainedEarnings.Account, RetainedEarnings.Account.Code, RetainedEarnings.Account.Description, RetainedEarnings.Account.Order, Constants.SystemTitle
UNION ALL
SELECT BalancesAndTurnovers.Account, BalancesAndTurnovers.AccountAccountType, BalancesAndTurnovers.Account.Code, BalancesAndTurnovers.Account.Description, BalancesAndTurnovers.Account.Order, FALSE, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr, BalancesAndTurnovers.AmountRCTurnoverDr, BalancesAndTurnovers.AmountRCTurnoverCr, BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr, BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr, Constants.SystemTitle FROM BalancesAndTurnovers AS BalancesAndTurnovers, Constants AS Constants WHERE NOT BalancesAndTurnovers.Account.RetainedEarnings AND CASE WHEN BalancesAndTurnovers.AccountAccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) AND BalancesAndTurnovers.AmountRCTurnoverDr = 0 AND BalancesAndTurnovers.AmountRCTurnoverCr = 0 THEN FALSE ELSE TRUE END
