dataSet.query:
SELECT TOP 1 ChartOfAccounts.Ref AS Account INTO RetainedEarningsAccount FROM ChartOfAccounts.ChartOfAccounts AS ChartOfAccounts WHERE ChartOfAccounts.RetainedEarnings = TRUE ;
//////////////////////////////////////////////////////////////////////////////// SELECT ChartOfAccounts.Ref AS Account, ISNULL(GeneralJournalFiscalOpenBalance.AmountRCSplittedBalanceDr, 0) AS AmountRCFiscalOpenSplittedBalanceDr, ISNULL(GeneralJournalFiscalOpenBalance.AmountRCSplittedBalanceCr, 0) AS AmountRCFiscalOpenSplittedBalanceCr INTO GeneralJournalFiscalOpenBalance FROM ChartOfAccounts.ChartOfAccounts AS ChartOfAccounts LEFT JOIN AccountingRegister.GeneralJournal.Balance({(&BeginOfFiscalYear)}, , , ) AS GeneralJournalFiscalOpenBalance ON ChartOfAccounts.Ref = GeneralJournalFiscalOpenBalance.Account ;
//////////////////////////////////////////////////////////////////////////////// SELECT ChartOfAccounts.Ref AS Account, SUM(ISNULL(GeneralJournalTurnovers.AmountRCTurnoverDr, 0)) AS AmountRCTurnoverDr, SUM(ISNULL(GeneralJournalTurnovers.AmountRCTurnoverCr, 0)) AS AmountRCTurnoverCr INTO AdjustingTurnovers FROM ChartOfAccounts.ChartOfAccounts AS ChartOfAccounts LEFT JOIN AccountingRegister.GeneralJournal.Turnovers({(&BeginOfPeriod)}, {(&EndOfPeriod)}, Record, , , ) AS GeneralJournalTurnovers ON ChartOfAccounts.Ref = GeneralJournalTurnovers.Account WHERE GeneralJournalTurnovers.Recorder REFS Document.GeneralJournalEntry AND GeneralJournalTurnovers.Recorder.Adjusting
GROUP BY ChartOfAccounts.Ref ;
//////////////////////////////////////////////////////////////////////////////// SELECT ChartOfAccounts.Ref AS Account, ChartOfAccounts.AccountType AS AccountAccountType, ISNULL(GeneralJournalFiscalOpenBalance.AmountRCFiscalOpenSplittedBalanceDr, 0) AS AmountRCFiscalOpenSplittedBalanceDr, ISNULL(GeneralJournalFiscalOpenBalance.AmountRCFiscalOpenSplittedBalanceCr, 0) AS AmountRCFiscalOpenSplittedBalanceCr, ISNULL(GeneralJournalBalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr, 0) AS AmountRCOpeningSplittedBalanceDr, ISNULL(GeneralJournalBalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr, 0) AS AmountRCOpeningSplittedBalanceCr, ISNULL(GeneralJournalBalancesAndTurnovers.AmountRCTurnoverDr, 0) - ISNULL(AdjustingTurnovers.AmountRCTurnoverDr, 0) AS AmountRCTurnoverDr, ISNULL(GeneralJournalBalancesAndTurnovers.AmountRCTurnoverCr, 0) - ISNULL(AdjustingTurnovers.AmountRCTurnoverCr, 0) AS AmountRCTurnoverCr, ISNULL(AdjustingTurnovers.AmountRCTurnoverDr, 0) AS AdjustingAmountRCTurnoverDr, ISNULL(AdjustingTurnovers.AmountRCTurnoverCr, 0) AS AdjustingAmountRCTurnoverCr, ISNULL(GeneralJournalBalancesAndTurnovers.AmountRCClosingSplittedBalanceDr, 0) AS AmountRCClosingSplittedBalanceDr, ISNULL(GeneralJournalBalancesAndTurnovers.AmountRCClosingSplittedBalanceCr, 0) AS AmountRCClosingSplittedBalanceCr INTO BalancesAndTurnovers {SELECT Account.*} FROM ChartOfAccounts.ChartOfAccounts AS ChartOfAccounts LEFT JOIN GeneralJournalFiscalOpenBalance AS GeneralJournalFiscalOpenBalance ON ChartOfAccounts.Ref = GeneralJournalFiscalOpenBalance.Account LEFT JOIN AccountingRegister.GeneralJournal.BalanceAndTurnovers({(&BeginOfPeriod)}, {(&EndOfPeriod)}, Auto, , , , ) AS GeneralJournalBalancesAndTurnovers ON ChartOfAccounts.Ref = GeneralJournalBalancesAndTurnovers.Account LEFT JOIN AdjustingTurnovers AS AdjustingTurnovers ON ChartOfAccounts.Ref = AdjustingTurnovers.Account {WHERE ChartOfAccounts.Ref.* AS Account}
INDEX BY AccountAccountType ;
//////////////////////////////////////////////////////////////////////////////// SELECT RetainedEarningsAccount.Account AS Account, BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr AS AmountRCOpeningSplittedBalanceDr, BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr AS AmountRCOpeningSplittedBalanceCr, 0 AS AmountRCTurnoverDr, 0 AS AmountRCTurnoverCr, 0 AS AdjustingAmountRCTurnoverDr, 0 AS AdjustingAmountRCTurnoverCr, BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr AS AmountRCClosingSplittedBalanceDr, BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr AS AmountRCClosingSplittedBalanceCr INTO RetainedEarnings FROM BalancesAndTurnovers AS BalancesAndTurnovers LEFT JOIN RetainedEarningsAccount AS RetainedEarningsAccount ON (TRUE) WHERE BalancesAndTurnovers.AccountAccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense))
UNION ALL
SELECT RetainedEarningsAccount.Account, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr, BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr, BalancesAndTurnovers.AmountRCTurnoverDr, BalancesAndTurnovers.AmountRCTurnoverCr, BalancesAndTurnovers.AdjustingAmountRCTurnoverDr, BalancesAndTurnovers.AdjustingAmountRCTurnoverCr, BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr, BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr FROM BalancesAndTurnovers AS BalancesAndTurnovers LEFT JOIN RetainedEarningsAccount AS RetainedEarningsAccount ON (TRUE) WHERE BalancesAndTurnovers.Account.RetainedEarnings ;
//////////////////////////////////////////////////////////////////////////////// SELECT RetainedEarnings.Account AS Account, VALUE(Enum.AccountTypes.Equity) AS AccountAccountType, RetainedEarnings.Account.Code AS AccountCode, RetainedEarnings.Account.Description AS AccountDescription, RetainedEarnings.Account.Order AS AccountOrder, CASE WHEN RetainedEarnings.Account IS NULL THEN TRUE ELSE FALSE END AS RetainedEarnings, CASE WHEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) > 0 THEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) ELSE 0 END AS AmountRCOpeningSplittedBalanceDr, CASE WHEN SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr) > 0 THEN 0 ELSE -(SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCOpeningSplittedBalanceCr)) END AS AmountRCOpeningSplittedBalanceCr, CASE WHEN SUM(RetainedEarnings.AmountRCTurnoverDr) - SUM(RetainedEarnings.AmountRCTurnoverCr) > 0 THEN SUM(RetainedEarnings.AmountRCTurnoverDr) - SUM(RetainedEarnings.AmountRCTurnoverCr) ELSE 0 END AS AmountRCTurnoverDr, CASE WHEN SUM(RetainedEarnings.AmountRCTurnoverDr) - SUM(RetainedEarnings.AmountRCTurnoverCr) > 0 THEN 0 ELSE -(SUM(RetainedEarnings.AmountRCTurnoverDr) - SUM(RetainedEarnings.AmountRCTurnoverCr)) END AS AmountRCTurnoverCr, CASE WHEN SUM(RetainedEarnings.AdjustingAmountRCTurnoverDr) - SUM(RetainedEarnings.AdjustingAmountRCTurnoverCr) > 0 THEN SUM(RetainedEarnings.AdjustingAmountRCTurnoverDr) - SUM(RetainedEarnings.AdjustingAmountRCTurnoverCr) ELSE 0 END AS AdjustingAmountRCTurnoverDr, CASE WHEN SUM(RetainedEarnings.AdjustingAmountRCTurnoverDr) - SUM(RetainedEarnings.AdjustingAmountRCTurnoverCr) > 0 THEN 0 ELSE -(SUM(RetainedEarnings.AdjustingAmountRCTurnoverDr) - SUM(RetainedEarnings.AdjustingAmountRCTurnoverCr)) END AS AdjustingAmountRCTurnoverCr, CASE WHEN SUM(RetainedEarnings.AmountRCClosingSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCClosingSplittedBalanceCr) > 0 THEN SUM(RetainedEarnings.AmountRCClosingSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCClosingSplittedBalanceCr) ELSE 0 END AS AmountRCClosingSplittedBalanceDr, CASE WHEN SUM(RetainedEarnings.AmountRCClosingSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCClosingSplittedBalanceCr) > 0 THEN 0 ELSE -(SUM(RetainedEarnings.AmountRCClosingSplittedBalanceDr) - SUM(RetainedEarnings.AmountRCClosingSplittedBalanceCr)) END AS AmountRCClosingSplittedBalanceCr, Constants.SystemTitle INTO TotalTable FROM RetainedEarnings AS RetainedEarnings, Constants AS Constants
GROUP BY RetainedEarnings.Account, RetainedEarnings.Account.Code, RetainedEarnings.Account.Description, RetainedEarnings.Account.Order, Constants.SystemTitle
UNION ALL
SELECT BalancesAndTurnovers.Account, BalancesAndTurnovers.AccountAccountType, BalancesAndTurnovers.Account.Code, BalancesAndTurnovers.Account.Description, BalancesAndTurnovers.Account.Order, FALSE, CASE WHEN BalancesAndTurnovers.AccountAccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) THEN CASE WHEN BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr) > 0 THEN BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr) ELSE 0 END ELSE CASE WHEN BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr > 0 THEN BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr ELSE 0 END END, CASE WHEN BalancesAndTurnovers.AccountAccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) THEN CASE WHEN BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr) > 0 THEN 0 ELSE -(BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr)) END ELSE CASE WHEN BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr > 0 THEN 0 ELSE -(BalancesAndTurnovers.AmountRCOpeningSplittedBalanceDr - BalancesAndTurnovers.AmountRCOpeningSplittedBalanceCr) END END, CASE WHEN BalancesAndTurnovers.AmountRCTurnoverDr - BalancesAndTurnovers.AmountRCTurnoverCr > 0 THEN BalancesAndTurnovers.AmountRCTurnoverDr - BalancesAndTurnovers.AmountRCTurnoverCr ELSE 0 END, CASE WHEN BalancesAndTurnovers.AmountRCTurnoverDr - BalancesAndTurnovers.AmountRCTurnoverCr > 0 THEN 0 ELSE -(BalancesAndTurnovers.AmountRCTurnoverDr - BalancesAndTurnovers.AmountRCTurnoverCr) END, CASE WHEN BalancesAndTurnovers.AdjustingAmountRCTurnoverDr - BalancesAndTurnovers.AdjustingAmountRCTurnoverCr > 0 THEN BalancesAndTurnovers.AdjustingAmountRCTurnoverDr - BalancesAndTurnovers.AdjustingAmountRCTurnoverCr ELSE 0 END, CASE WHEN BalancesAndTurnovers.AdjustingAmountRCTurnoverDr - BalancesAndTurnovers.AdjustingAmountRCTurnoverCr > 0 THEN 0 ELSE -(BalancesAndTurnovers.AdjustingAmountRCTurnoverDr - BalancesAndTurnovers.AdjustingAmountRCTurnoverCr) END, CASE WHEN BalancesAndTurnovers.AccountAccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) THEN CASE WHEN BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr) > 0 THEN BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr) ELSE 0 END ELSE CASE WHEN BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr > 0 THEN BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr ELSE 0 END END, CASE WHEN BalancesAndTurnovers.AccountAccountType IN (VALUE(Enum.AccountTypes.Income), VALUE(Enum.AccountTypes.CostOfSales), VALUE(Enum.AccountTypes.Expense), VALUE(Enum.AccountTypes.OtherExpense), VALUE(Enum.AccountTypes.OtherIncome), VALUE(Enum.AccountTypes.IncomeTaxExpense)) THEN CASE WHEN BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr) > 0 THEN 0 ELSE -(BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceDr - (BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr - BalancesAndTurnovers.AmountRCFiscalOpenSplittedBalanceCr)) END ELSE CASE WHEN BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr > 0 THEN 0 ELSE -(BalancesAndTurnovers.AmountRCClosingSplittedBalanceDr - BalancesAndTurnovers.AmountRCClosingSplittedBalanceCr) END END, Constants.SystemTitle FROM BalancesAndTurnovers AS BalancesAndTurnovers, Constants AS Constants WHERE NOT BalancesAndTurnovers.Account.RetainedEarnings ;
//////////////////////////////////////////////////////////////////////////////// SELECT TotalTable.Account, TotalTable.AccountAccountType, TotalTable.AccountCode, TotalTable.AccountDescription, TotalTable.AccountOrder, TotalTable.RetainedEarnings, TotalTable.AmountRCOpeningSplittedBalanceDr, TotalTable.AmountRCOpeningSplittedBalanceCr, TotalTable.AmountRCTurnoverDr, TotalTable.AmountRCTurnoverCr, TotalTable.AdjustingAmountRCTurnoverDr, TotalTable.AdjustingAmountRCTurnoverCr, TotalTable.AmountRCClosingSplittedBalanceDr, TotalTable.AmountRCClosingSplittedBalanceCr, TotalTable.SystemTitle FROM TotalTable AS TotalTable WHERE CASE WHEN &AllAccounts THEN TRUE ELSE CASE WHEN TotalTable.AmountRCOpeningSplittedBalanceDr = 0 AND TotalTable.AmountRCOpeningSplittedBalanceCr = 0 AND TotalTable.AmountRCTurnoverDr = 0 AND TotalTable.AmountRCTurnoverCr = 0 AND TotalTable.AdjustingAmountRCTurnoverDr = 0 AND TotalTable.AdjustingAmountRCTurnoverCr = 0 AND TotalTable.AmountRCClosingSplittedBalanceDr = 0 AND TotalTable.AmountRCClosingSplittedBalanceCr = 0 THEN FALSE ELSE TRUE END END
