dataSet.query:
SELECT Categories.Ref AS Ref, Categories.Threshold AS Threshold, CASE WHEN Categories.AmountSign THEN -1 ELSE 1 END AS AmountSign INTO Categories FROM Catalog.USTaxCategories1099 AS Categories ;
//////////////////////////////////////////////////////////////////////////////// SELECT CashFlowData.Account.Category1099 AS Category, CashFlowData.Company AS Vendor, Categories.Threshold AS Threshold, SUM(Categories.AmountSign * CASE WHEN CashFlowData.RecordType = VALUE(AccumulationRecordType.Receipt) THEN CashFlowData.AmountRC ELSE CashFlowData.AmountRC * -1 END) AS Turnover INTO Turnovers FROM AccumulationRegister.CashFlowData AS CashFlowData LEFT JOIN Categories AS Categories ON CashFlowData.Account.Category1099 = Categories.Ref WHERE CashFlowData.Period >= &BeginOfPeriod AND CashFlowData.Period <= &EndOfPeriod AND NOT CashFlowData.Account.Category1099 = VALUE(Catalog.USTaxCategories1099.EmptyRef) AND CashFlowData.PaymentMethod.ExcludeFrom1099 = FALSE
GROUP BY CashFlowData.Account.Category1099, CashFlowData.Company, Categories.Threshold ;
//////////////////////////////////////////////////////////////////////////////// SELECT Turnovers.Vendor AS Vendor, MAX(Turnovers.Turnover - Turnovers.Threshold) AS ThresholdOvercome INTO VendorStatuses FROM Turnovers AS Turnovers
GROUP BY Turnovers.Vendor ;
//////////////////////////////////////////////////////////////////////////////// SELECT CashFlowData.Account.Category1099 AS Category, CashFlowData.Company AS Vendor, CashFlowData.Document AS Document, CashFlowData.PaymentMethod.ExcludeFrom1099 AS PaymentExcluded, CASE WHEN Turnovers.Turnover - Turnovers.Threshold IS NULL THEN FALSE WHEN Turnovers.Turnover - Turnovers.Threshold >= 0 THEN TRUE ELSE FALSE END AS ExceedsThreshold, Categories.AmountSign * CASE WHEN CashFlowData.RecordType = VALUE(AccumulationRecordType.Receipt) THEN CashFlowData.AmountRC ELSE CashFlowData.AmountRC * -1 END AS Amount INTO Amounts FROM AccumulationRegister.CashFlowData AS CashFlowData LEFT JOIN VendorStatuses AS VendorStatuses ON (VendorStatuses.Vendor = CashFlowData.Company) LEFT JOIN Turnovers AS Turnovers ON (Turnovers.Category = CashFlowData.Account.Category1099) AND (Turnovers.Vendor = CashFlowData.Company) LEFT JOIN Categories AS Categories ON (Categories.Ref = CashFlowData.Account.Category1099) WHERE CashFlowData.Period >= &BeginOfPeriod AND CashFlowData.Period <= &EndOfPeriod AND NOT CashFlowData.Account.Category1099 = VALUE(Catalog.USTaxCategories1099.EmptyRef) AND CASE WHEN NOT CashFlowData.Company.Vendor1099 THEN 3 WHEN VendorStatuses.ThresholdOvercome >= 0 THEN 1 ELSE 2 END = &Mode ;
//////////////////////////////////////////////////////////////////////////////// SELECT TRUE AS PaymentExcluded INTO PaymentStatuses
UNION ALL
SELECT FALSE ;
//////////////////////////////////////////////////////////////////////////////// SELECT DISTINCT Vendors.Ref AS Ref, Vendors.Description AS Name, Vendors.FederalIDType AS FederalIdType, Vendors.FederalIDType.Order + 1 AS FederalIdTypeNo, Vendors.USTaxID AS FederalIdNo, Vendors.FullName AS FullName, Addresses.AddressLine1 + CASE WHEN Addresses.AddressLine2 = "" THEN "" ELSE ", " + Addresses.AddressLine2 END + CASE WHEN Addresses.AddressLine3 = "" THEN "" ELSE ", " + Addresses.AddressLine3 END AS Address, Addresses.City AS City, Addresses.State AS State, Addresses.State.Code AS StateCode, Addresses.ZIP AS Zip, Addresses.Email AS Email INTO Vendors FROM Amounts AS Amounts LEFT JOIN Catalog.Companies AS Vendors ON (Vendors.Ref = Amounts.Vendor) LEFT JOIN Catalog.Addresses AS Addresses ON (Addresses.Owner = Vendors.Ref) AND (Addresses.DefaultBilling) ;
//////////////////////////////////////////////////////////////////////////////// SELECT Boxes.Ref AS Category, Boxes.Code AS BoxNum, Boxes.Threshold AS Threshold, PaymentStatuses.PaymentExcluded AS PaymentExcluded, Vendors.Ref AS Vendor, Vendors.Name AS VendorName, Vendors.FederalIdType AS VendorFederalIdType, Vendors.FederalIdTypeNo AS VendorFederalIdTypeNo, Vendors.FederalIdNo AS VendorFederalIdNo, Vendors.FullName AS VendorFullName, Vendors.Address AS VendorAddress, Vendors.City AS VendorCity, Vendors.State AS VendorState, Vendors.StateCode AS VendorStateCode, Vendors.Zip AS VendorZip, Vendors.Email AS VendorEmail, Amounts.Document AS Document, Amounts.ExceedsThreshold AS ExceedsThreshold, Amounts.Amount AS Amount, NULL AS AccounNumber, NULL AS OfficeCode, NULL AS Additional {SELECT Category.*, Vendor.*, Document.*, Amount} FROM Catalog.USTaxCategories1099 AS Boxes LEFT JOIN PaymentStatuses AS PaymentStatuses ON (TRUE) LEFT JOIN Amounts AS Amounts ON (Amounts.Category = Boxes.Ref) AND (Amounts.PaymentExcluded = PaymentStatuses.PaymentExcluded) LEFT JOIN Vendors AS Vendors ON (Vendors.Ref = Amounts.Vendor) {WHERE Boxes.Ref.* AS Category, Amounts.Vendor.* AS Vendor, Amounts.Document.* AS Document, Amounts.Amount AS Amount}
